import React, { useState, useEffect } from "react";
import styled from "styled-components";
import {
  HiOutlinePlusCircle,
  HiOutlineMinusCircle,
  HiOutlineX,
} from "react-icons/hi";

export default function Cart() {
  const [cart, setCart] = useState();
  const [totalPrice, setTotalPrice] = useState(0);

  useEffect(() => {
    const fetchData = async () => {
      const result = await fetch(`/Data/cart-Data.json`);
      const json = await result.json();
      setCart(json.data);
    };
    fetchData();
  }, []);

  const totalPricer = () => {
    setTotalPrice(
      cart?.products.map((item) => {
        const prices = totalPrice + item.price;
        return totalPrice;
      })
    );
  };

  console.log(totalPrice);
  return (
    <>
      <Nav>
        <span>진희 Nav</span>
      </Nav>
      <Section>
        <CartContainer>
          <h2>Shopping bag</h2>
          {cart?.products.map((item) => {
            return (
              <CartItem>
                <CartImgBox>
                  <CartImg src={item.main_image}></CartImg>
                </CartImgBox>
                <CountBox>
                  <MinusCount />
                  <span>{item.count}</span>
                  <Xcount /> <PlusCount />
                </CountBox>
                <TitleBox>
                  <span>{item.title}</span>
                </TitleBox>
                <TitleBox>
                  <span>{item.serial_num}</span>
                </TitleBox>
                <TitleBox>
                  <span>
                    {item.size} {item.color}
                  </span>
                </TitleBox>
                <PriceBox>
                  <span>$ {item.price}</span>
                </PriceBox>
                <TotalPriceBox>
                  <span>Total $ {item.price * item.count}</span>
                </TotalPriceBox>
              </CartItem>
            );
          })}
        </CartContainer>
        <PriceContainer>
          <h2>Bag Total</h2>
          <OrderContainer>
            <OrderPriceBox>
              <span>Order value</span>
              <span>$ 617</span>
            </OrderPriceBox>
            <OrderPriceBox>
              <span>Delivery</span>
              <span>FREE</span>
            </OrderPriceBox>
            <DiscountBox>
              <span>Do you have a discount code?</span>
              <span></span>
            </DiscountBox>
          </OrderContainer>
          <TotalBox>
            <TotalText>TOTAL</TotalText>
            <TotalPrice>$ {totalPricer()}</TotalPrice>
          </TotalBox>
          <AddBtnBox>
            <span>Checkout</span>
          </AddBtnBox>
          <OrderDescript>
            <div>
              <span>Read more about how to return an item here</span>
              <span>Free shipping on orders over 200 USD*</span>
              <span>Read more about delivery options here</span>
            </div>
            <div>
              <span>Secure payment</span>
              <span>Cards, Klarna & PayPal. Read more about Payment.</span>
              <span>
                In order to use the PayPal option, pop-ups need to be enabled in
                your browser settings.
              </span>
            </div>
            <div>
              <span>Do you need help?</span>
              <span>Go to Customer service</span>
              <span>
                ARKET will process your personal data to manage your order, in
                accordance with ARKET Privacy Notice
              </span>
            </div>
          </OrderDescript>
        </PriceContainer>
      </Section>
    </>
  );
}

