import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";
import { IoIosArrowBack, IoIosArrowForward } from "react-icons/io";
import { BsArrowRightShort } from "react-icons/bs";
import OrderContainer from "./components/OrderContainer";
import ProductDetail from "./components/ProductDetail";
import ProductDescription from "./components/ProductDescription";

export default function ItemDetail() {
  const [product, setProduct] = useState();

  useEffect(() => {
    const fetchData = async () => {
      const result = await fetch(`http://localhost:3000/Data/Item-Detail.json`);
      const json = await result.json();
      setProduct(json.product);
    };
    fetchData();
  }, []);

  return (
    <>
      <Nav />
      <Section>
        <MainContent>
          <ItemInfoBox>
            <ItemRoot>
              <span>
                ARKET
                <BsArrowRightShort
                  style={{ verticalAlign: "text-top", marginLeft: "3px" }}
                />
                Women
                <BsArrowRightShort
                  style={{ verticalAlign: "text-top", marginLeft: "3px" }}
                />
                {product?.Type}
                <BsArrowRightShort
                  style={{ verticalAlign: "text-top", marginLeft: "3px" }}
                />
                {product?.Title}
              </span>
            </ItemRoot>
            <ItemColors>
              <ItemInfoCategory>COLORS</ItemInfoCategory>
              <ItemCategoryContainer>
                {product?.Color.map((productColor, i) => {
                  return (
                    <ItemCategoryBox key={i}>
                      <Link to={`/item-detail${productColor.Color_Url}`}>
                        <ItemColorImg src={productColor.Color_Img} />
                        <p>{productColor.Color_Name}</p>
                      </Link>
                    </ItemCategoryBox>
                  );
                })}
              </ItemCategoryContainer>
            </ItemColors>

            <ItemMaterials>
              <ItemInfoCategory>MATERIALS</ItemInfoCategory>
              <ItemCategoryContainer>
                <ItemCategoryBox>
                  <ItemMaterialImg src={product?.Material.Material_Img} />
                  <p>{product?.Material.Material_Name}</p>
                </ItemCategoryBox>
              </ItemCategoryContainer>
            </ItemMaterials>
          </ItemInfoBox>
          <ItemImgBox>
            <StyledSlider {...SETTINGS}>
              {product?.Images.map((productImages, i) => {
                return (
                  <div>
                    <MainImg src={productImages.Img_Url} key={i} />
                  </div>
                );
              })}
            </StyledSlider>
          </ItemImgBox>
          <ItemDetailInfoBox>
            <OrderContainer product={product} />
            <ProductDetail product={product} />
            <ProductDescription />
          </ItemDetailInfoBox>
        </MainContent>
      </Section>
    </>
  );
}

const Nav = styled.div`
  height: 60px;
  background-color: yellow;
`;

const Section = styled.section`
  margin: 0 auto;
  margin-left: 20px;
  margin-right: 20px;
  padding-top: 20px;
`;

const MainContent = styled.div`
  display: flex;
  justify-content: space-between;
`;

const ItemInfoBox = styled.div`
  max-width: 250px;
  width: calc(50% - 299px);
`;

const ItemRoot = styled.div`
  height: 17px;
  text-overflow: hidden;
  white-space: nowrap;
  span {
    font-size: 14px;
  }
`;

const ItemDetailInfoBox = styled.div`
  max-width: 299px;
  width: 100%;
`;

const ItemCategoryContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
`;

const ItemInfoCategory = styled.h4`
  font-size: 13px;
  margin-bottom: 10px;
`;

const ItemCategoryBox = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column;
  justify-content: space-between;
  width: 56px;
  height: 90px;
  padding-bottom: 5px;
  cursor: pointer;

  p {
    font-size: 11px;
    text-align: center;
  }

  &:hover {
    p {
      opacity: 0.6;
    }
  }
`;

const ItemColors = styled.div`
  margin-top: 15px;
  padding-bottom: 4px;
  padding-top: 20px;
  border-top: 2px solid #000;
`;

const ItemColorImg = styled.img`
  width: 49px;
  height: 65px;
  src: "/Images/test_jean.jpg";
`;

const ItemMaterials = styled.div`
  margin-top: 15px;
  margin-bottom: 15px;
  padding-top: 15px;
  padding-bottom: 15px;
  border-top: 2px solid #000;
`;

const ItemMaterialImg = styled.img`
  width: 49px;
  height: 49px;
  border-radius: 50%;
  border: 1px solid black;
`;

const ItemImgBox = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: calc(100vh - 56px - 40px);
`;

const ArrowBox = styled.button`
  cursor: pointer;
  svg {
    width: 49px;
    height: 92px;
  }
`;

const MainImg = styled.img`
  padding-top: 10px;
  padding-bottom: 30px;
  max-height: 100%;
  max-width: 100%;
  width: 100%;
`;

const SETTINGS = {
  dots: false,
  infinite: true,
  speed: 500,
  slidesToShow: 1,
  slidesToScroll: 1,
  arrows: true,
  draggable: true,
  responsive: [
    {
      breakpoint: 960,
      settings: {
        slidesToShow: 1,
      },
    },
    {
      breakpoint: 768,
      settings: {
        slidesToShow: 1,
      },
    },
  ],
  nextArrow: <NextBtn />,
  prevArrow: <PrevBtn />,
};

const StyledSlider = styled(Slider)`
  display: flex;
  align-items: center;
  .slick-list {
    width: 500px;
  }
`;

function PrevBtn(props) {
  const { style, onClick } = props;
  return (
    <ArrowBox style={{ ...style, display: "block" }} onClick={onClick}>
      <IoIosArrowBack to="prev" />
    </ArrowBox>
  );
}

function NextBtn(props) {
  const { style, onClick } = props;
  return (
    <ArrowBox style={{ ...style, display: "block" }} onClick={onClick}>
      <IoIosArrowForward to="next" />
    </ArrowBox>
  );
}
