/* eslint-disable react-hooks/exhaustive-deps */
import React, { useState, useEffect } from "react";
import styled from "styled-components";
import CartItem from "./components/CartItem";
import PriceContainer from "./components/PriceContainer";


export default function Cart() {
  const [cart, setCart] = useState();
  const [totalPrice, setTotalPrice] = useState(0);
  const [isToken, setIsToken] = useState();

  useEffect(() => {
    fetchCart();
  }, []);

  const fetchCart = async () => {
    const result = await fetch(`/Data/cart-Data.json`);
    const {data} = await result.json();
    setCart(data);
  };

  const totalCalc = () => {
    const price = cart?.products.reduce((accumulator, item) => {
      return accumulator + item.price * item.count;
    }, 0);
    setTotalPrice(price);
  }

  useEffect(() => {
    totalCalc()
  }, [cart, totalCalc]);

  const deleteCart = (i) => {
    const cartList = {...cart};
    const updateItem  =cartList.products.filter(item => item.id !== cart.products[i].id)
    cartList.products= updateItem
    setCart(cartList)
  }

  const fetchIncrease = (i) => {
    const result = fetch(`/Data/cart-Data.json`, {
      method: "PATCH",
      header: JSON.stringify({Authorization: localStorage.getItem("token")}),
      body: JSON.stringify({
        cart_id: cart.products[i].cart_id,
        product_id: `/Data/cart-Data.json${cart.products[i].id}`,
        type: "increaseCount"
      }),
    });
    const {response} = result.json();
  }



  const decreaseCount = (i) => {
    const cartList = {...cart};
    if(cartList.products[i].count !== 1){
      const updateItem = {
        "id": cart.products[i].id,
        "title": cart.products[i].title,
        "count": cart.products[i].count -1,
        "price": cart.products[i].price,
        "size": cart.products[i].size,
        "color": cart.products[i].color,
        "serial_num": cart.products[i].serial_num,
        "main_image": cart.products[i].main_image
      }
      cartList.products[i] = updateItem
      setCart(cartList)
    }
    else {
      deleteCart(i)
    }

  }

  const increaseCount = (i) => {
    const cartList = {...cart};
    const updateItem = {
      "id": cart.products[i].id,
      "title": cart.products[i].title,
      "count": cart.products[i].count +1,
      "price": cart.products[i].price,
      "size": cart.products[i].size,
      "color": cart.products[i].color,
      "serial_num": cart.products[i].serial_num,
      "main_image": cart.products[i].main_image
    }
    cartList.products[i] = updateItem
    setCart(cartList)
  }

  return (
    <>
      <Nav>
        <span>진희 Nav</span>
      </Nav>
      <Section>
        <CartContainer>
          <h2>Shopping bag</h2>
          {cart?.products.map((item, i) => {
            return (
              <CartItem 
              cart={cart} 
              i={i} 
              item={item} 
              decreaseCount={decreaseCount} 
              increaseCount={increaseCount}
              deleteCart={deleteCart}
              />
            );
          })}
        </CartContainer>
        <PriceContainer totalPrice={totalPrice}/>
      </Section>
    </>
  );
}

const Nav = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 60px;
  background-color: yellow;
  text-align: center;
  span {
    font-size: 40px;
  }
`;

const Section = styled.section`
  display: flex;
  justify-content: center;
  max-width: 1000px;
  margin: 0 auto;
`;

const CartContainer = styled.div`
  margin-right: 40px;
  width: calc(66.66% - 20px);
  border-bottom: 2px solid black;
  margin-bottom: 100px;
  height: 100%;
  h2 {
    margin-top: 20px;
    padding-bottom: 13px;
    font-size: 20px;
    border-bottom: 2px solid black;
  }
`;
