import React, { useState, useEffect } from "react";
import styled, { css } from "styled-components";
import { useAsync } from "react-async";
import { Link } from "react-router-dom";
// import { useAsync } from "react-async";
import Slider from "react-slick";
import { IoIosArrowBack, IoIosArrowForward } from "react-icons/io";
import OrderContainer from "./components/OrderContainer";
import ProductDetail from "./components/ProductDetail";
import ProductDescription from "./components/ProductDescription";

export default function ItemDetail() {
  const [product, SetProduct] = useState();

  useEffect(() => {
    const fetchData = async () => {
      const result = await fetch(`http://localhost:3000/Data/Item-Detail.json`);
      const json = await result.json();
      SetProduct(json.product);
    };
    fetchData();
  }, []);

  console.log(product);

  return (
    <>
      <Nav />
      <Section>
        <MainContent>
          {/* 상세 페이지 왼쪽 칼라 천 부분 */}
          <ItemInfoBox>
            <ItemColors>
              <ItemInfoCategory>COLORS</ItemInfoCategory>
              <ItemCategoryContainer>
                {product?.Color.map((productColor, i) => {
                  return (
                    <ItemCategoryBox>
                      <Link to={productColor.Color_Url}>
                        <ItemColorImg
                          src={productColor.Color_Img}
                          key={productColor.i}
                        ></ItemColorImg>
                        <p>{productColor.Color_Name}</p>
                      </Link>
                    </ItemCategoryBox>
                  );
                })}
              </ItemCategoryContainer>
            </ItemColors>

            <ItemMaterials>
              <ItemInfoCategory>MATERIALS</ItemInfoCategory>
              <ItemCategoryContainer>
                <ItemCategoryBox>
                  <ItemMaterialImg
                    src={product?.Material.Material_Img}
                  ></ItemMaterialImg>
                  <p>{product?.Material.Material_Name}</p>
                </ItemCategoryBox>
              </ItemCategoryContainer>
            </ItemMaterials>
          </ItemInfoBox>
          {/* 가운데 이미지 부분 */}
          <ItemImgBox>
            <MainImgLeft>
              <IoIosArrowBack />
            </MainImgLeft>
            <Slider {...SETTINGS}>
              {/* <div> */}
              {product?.Images.map((productImages, i) => {
                return <MainImg src={productImages.Img_Url} />;
              })}
              {/* <MainImg src="/Images/flower_1.jpg" /> */}
              {/* </div>
              <div> */}
              {/* <MainImg src="/Images/flower_2.jpg" /> */}
              {/* </div>
              <div> */}
              {/* <MainImg src="/Images/flower_3.jpg" /> */}
              {/* </div> */}
            </Slider>

            <MainImgRight>
              <IoIosArrowForward />
            </MainImgRight>
          </ItemImgBox>
          {/* 상세 페이지 오른쪽 사이즈 주문 부분 */}
          <ItemDetailInfoBox>
            <OrderContainer product={product} />
            <ProductDetail product={product} />

            <ProductDescription product={product} />
          </ItemDetailInfoBox>
        </MainContent>
      </Section>
    </>
  );
}

const Nav = styled.div`
  height: 60px;
  background-color: yellow;
`;

const Section = styled.section`
  margin: 0 auto;
  margin-left: 20px;
  margin-right: 20px;
  padding-top: 20px;
`;

const MainContent = styled.div`
  display: flex;
  justify-content: space-between;
  /* flex-wrap:wrap; */
`;

const ItemInfoBox = styled.div`
  max-width: 250px;
  width: calc(50% - 299px);
  margin-top: 37px;
  border-top: 2px solid #000;
`;

const ItemDetailInfoBox = styled.div`
  max-width: 299px;
  width: 100%;
`;

const ItemCategoryContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
`;

const ItemInfoCategory = styled.h4`
  font-size: 13px;
  margin-bottom: 10px;
`;

const ItemCategoryBox = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column;
  justify-content: space-between;
  width: 56px;
  height: 90px;
  padding-bottom: 5px;
  cursor: pointer;

  p {
    font-size: 11px;
    text-align: center;
  }

  &:hover {
    p {
      opacity: 0.6;
    }
  }
`;

const ItemColors = styled.div`
  margin-top: 15px;
  padding-bottom: 4px;
`;

const ItemColorImg = styled.img`
  /* src: ("/Images/test_jean.jpg"); */
  width: 49px;
  height: 65px;
  src: "/Images/test_jean.jpg";
`;

const ItemColorName = styled.p`
  font-size: 11px;
  text-align: center;
`;

const ItemMaterials = styled.div`
  margin-top: 15px;
  margin-bottom: 15px;
  padding-top: 15px;
  padding-bottom: 15px;
  border-top: 2px solid #000;
  /* border-bottom: 2px solid #000; */
`;

const ItemMaterialImg = styled.img`
  /* src: ("/Images/test_jean.jpg"); */
  width: 49px;
  height: 49px;
  border-radius: 50%;
  border: 1px solid black;
`;

const ItemImgBox = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: calc(100vh - 56px - 40px);
`;

const MainImgLeft = styled.button`
  svg {
    width: 49px;
    height: 92px;
  }
`;

const MainImgRight = styled.button`
  svg {
    width: 49px;
    height: 92px;
  }
`;

const MainImg = styled.img`
  /* margin-top: 100px; */
  padding-top: 10px;
  padding-bottom: 30px;
  max-height: 100%;
  max-width: 100%;
`;

const SETTINGS = `
  dots: false,
  infinite: true,
  speed: 500,
  slidesToShow: 1,
  slidesToScroll: 1
`;
