import React from "react";
import styled, { css } from "styled-components";
import { IoIosArrowBack, IoIosArrowForward } from "react-icons/io";

export default function ItemDetail() {
  return (
    <Section>
      <MainContent>
        {/* 상세 페이지 왼쪽 칼라 천 부분 */}
        <ItemInfoBox>
          <ItemColors>
            <ItemInfoCategory>COLORS</ItemInfoCategory>
            <ItemCategoryContainer>
              <ItemCategoryBox>
                <ItemColorImg src="/Images/test_jean.jpg"></ItemColorImg>
                <ItemColorName>Black</ItemColorName>
              </ItemCategoryBox>
              <ItemCategoryBox>
                <ItemColorImg src="/Images/test_jean.jpg"></ItemColorImg>
                <ItemColorName>Pink</ItemColorName>
              </ItemCategoryBox>
              <ItemCategoryBox>
                <ItemColorImg src="/Images/test_jean.jpg"></ItemColorImg>
                <ItemColorName>Black</ItemColorName>
              </ItemCategoryBox>
              <ItemCategoryBox>
                <ItemColorImg src="/Images/test_jean.jpg"></ItemColorImg>
                <ItemColorName>Black</ItemColorName>
              </ItemCategoryBox>
              <ItemCategoryBox>
                <ItemColorImg src="/Images/test_jean.jpg"></ItemColorImg>
                <ItemColorName>Black</ItemColorName>
              </ItemCategoryBox>
            </ItemCategoryContainer>
          </ItemColors>

          <ItemMaterials>
            <ItemInfoCategory>MATERIALS</ItemInfoCategory>
            <ItemCategoryContainer>
              <ItemCategoryBox>
                <ItemMaterialImg src="/Images/MATERIALS_1.jpg"></ItemMaterialImg>
                <ItemColorName>Black</ItemColorName>
              </ItemCategoryBox>
            </ItemCategoryContainer>
          </ItemMaterials>
        </ItemInfoBox>
        {/* 가운데 이미지 부분 */}
        <ItemImgBox>
          <MainImgLeft>
            <IoIosArrowBack />
          </MainImgLeft>

          <MainImg src="/Images/model.jpg" />

          <MainImgRight>
            <IoIosArrowForward />
          </MainImgRight>
        </ItemImgBox>
        {/* 상세 페이지 오른쪽 사이즈 주문 부분 */}
        <ItemDetailInfoBox>
          <OrderContainer></OrderContainer>
        </ItemDetailInfoBox>
      </MainContent>
    </Section>
  );
}

const Section = styled.section`
  margin: 0 auto;
  margin-left: 20px;
  margin-right: 20px;
  padding-top: 20px;
`;

const MainContent = styled.div`
  display: flex;
  justify-content: space-between;
  /* flex-wrap:wrap; */
`;

const ItemInfoBox = styled.div`
  max-width: 250px;
  width: calc(50% - 299px);
  margin-top: 37px;
  border-top: 2px solid #000;
`;

const ItemDetailInfoBox = styled.div`
  max-width: 299px;
  width: 100%;
  background: green;
`;

const ItemCategoryContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
`;

const ItemInfoCategory = styled.h4`
  font-size: 13px;
  margin-bottom: 10px;
`;

const ItemCategoryBox = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column;
  justify-content: space-between;
  width: 56px;
  height: 90px;
  padding-bottom: 5px;
`;

const ItemColors = styled.div`
  margin-top: 15px;
  padding-bottom: 4px;
`;

const ItemColorImg = styled.img`
  /* src: ("/Images/test_jean.jpg"); */
  width: 49px;
  height: 65px;
  src: "/Images/test_jean.jpg";
`;

const ItemColorName = styled.p`
  font-size: 11px;
  text-align: center;
`;

const ItemMaterials = styled.div`
  margin-top: 15px;
  margin-bottom: 15px;
  padding-top: 15px;
  padding-bottom: 15px;
  border-top: 2px solid #000;
  border-bottom: 2px solid #000;
`;

const ItemMaterialImg = styled.img`
  /* src: ("/Images/test_jean.jpg"); */
  width: 49px;
  height: 49px;
  border-radius: 50%;
  border: 1px solid black;
`;

const ItemImgBox = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: calc(100vh - 56px - 40px);
`;

const MainImgLeft = styled.button`
  svg {
    width: 49px;
    height: 92px;
  }
`;

const MainImgRight = styled.button`
  svg {
    width: 49px;
    height: 92px;
  }
`;

const MainImg = styled.img`
  margin-top: 100px;
  padding-top: 10px;
  padding-bottom: 30px;
  max-height: 100%;
  max-width: 100%;
`;

const OrderContainer = styled.div``;

const ProductMaterials = styled.div``;
