import React, { useState, useEffect } from "react";
import styled from "styled-components";
import {
  HiOutlinePlusCircle,
  HiOutlineMinusCircle,
  HiOutlineX,
} from "react-icons/hi";

import OrderDescript from "./components/OrderDescript";
import CartItem from "./components/CartItem";


export default function Cart() {
  const [cart, setCart] = useState();

  const [totalPrice, setTotalPrice] = useState(0);

  useEffect(() => {
    const fetchData = async () => {
      const result = await fetch(`/Data/cart-Data.json`);
      const json = await result.json();
      setCart(json.data);
    };
    fetchData();
  }, []);

  useEffect(() => {
    // eslint-disable-next-line no-unused-expressions
    const price = cart?.products.reduce((accumulator, item) => {
      return accumulator + item.price * item.count;
    }, 0);
    setTotalPrice(price);
  }, [cart]);

  const decreaseCount = (i) => {
    const carts = {...cart};
    const updateCart = {
      "id": cart.products[i].id,
      "title": cart.products[i].title,
      "count": cart.products[i].count -1,
      "price": cart.products[i].price,
      "size": cart.products[i].size,
      "color": cart.products[i].color,
      "serial_num": cart.products[i].serial_num,
      "main_image": cart.products[i].main_image
    }
    carts.products[i] = updateCart
    setCart(carts)
  }

  const increaseCount = (i) => {
    const carts = {...cart};
    const updateCart = {
      "id": cart.products[i].id,
      "title": cart.products[i].title,
      "count": cart.products[i].count +1,
      "price": cart.products[i].price,
      "size": cart.products[i].size,
      "color": cart.products[i].color,
      "serial_num": cart.products[i].serial_num,
      "main_image": cart.products[i].main_image
    }
    carts.products[i] = updateCart
    setCart(carts)
  }

  return (
    <>
      <Nav>
        <span>진희 Nav</span>
      </Nav>
      <Section>
        <CartContainer>
          <h2>Shopping bag</h2>
          {cart?.products.map((item, i) => {
            return (
              <CartItem cart={cart} i={i} item={item} decreaseCount={decreaseCount} increaseCount={increaseCount}/>
            //     key={i}
            //     style={{
            //       borderRight: i % 2 === 0 && "1px solid #000",
            //       borderBottom:
            //         (cart.products.length - 2 === 0 ||
            //           cart.products.length - 2 < i) &&
            //         "none",
            //     }}
            //   >
            //     <CartImgBox>
            //       <span>
            //         <Xbox>
            //           <span>X</span>
            //         </Xbox>
            //       </span>
            //       <CartImg src={item.main_image}></CartImg>
            //     </CartImgBox>
            //     <CountBox>
            //       <MinusCount decreaseCount={() => decreaseCount(i)} count={item.count}/>
            //       <span>{item.count}</span>
            //       <Xcount /> 
            //       <PlusCount increaseCount={() => increaseCount(i)} />
            //     </CountBox>
            //     <TitleBox>
            //       <span>{item.title}</span>
            //     </TitleBox>
            //     <TitleBox>
            //       <span>{item.serial_num}</span>
            //     </TitleBox>
            //     <TitleBox>
            //       <span>
            //         {item.size} {item.color}
            //       </span>
            //     </TitleBox>
            //     <PriceBox>
            //       <span>$ {item.price}</span>
            //     </PriceBox>
            //     <TotalPriceBox>
            //       <span>Total $ {item.price * item.count}</span>
            //     </TotalPriceBox>
            //   </CartItem>
            // );
            );
          })}
        </CartContainer>
        <PriceContainer>
          <h2>Bag Total</h2>
          <OrderContainer>
            <OrderPriceBox>
              <span>Order value</span>
              <span>$ {totalPrice}</span>
            </OrderPriceBox>
            <OrderPriceBox>
              <span>Delivery</span>
              <span>FREE</span>
            </OrderPriceBox>
            <DiscountBox>
              <span>Do you have a discount code?</span>
              <span></span>
            </DiscountBox>
          </OrderContainer>
          <TotalBox>
            <TotalText>TOTAL</TotalText>
            <TotalPrice>${totalPrice}</TotalPrice>
          </TotalBox>
          <AddBtnBox>
            <span>Checkout</span>
          </AddBtnBox>
          <OrderDescript />
        </PriceContainer>
      </Section>
    </>
  );
}

