import React, { useState, useEffect } from "react";
import styled, { css } from "styled-components";
import { Link } from "react-router-dom";
import Slider from "react-slick";
import { IoIosArrowBack, IoIosArrowForward } from "react-icons/io";
import { AiOutlinePlusCircle } from "react-icons/ai";

export default function ItemDetail() {
  const [product, SetProduct] = useState();

  useEffect(() => {
    fetch("http://localhost:3000/Data/Item-Detail.json", {
      method: "GET",
      // }).then((res) => console.log(res));
    })
      .then((res) => res.json())
      .then((res) => {
        SetProduct(res.product);
      });
  }, []);

  console.log(product);

  return (
    <>
      <Nav />
      <Section>
        <MainContent>
          {/* 상세 페이지 왼쪽 칼라 천 부분 */}
          <ItemInfoBox>
            <ItemColors>
              <ItemInfoCategory>COLORS</ItemInfoCategory>
              <ItemCategoryContainer>
                {product?.Color.map((el, i) => {
                  return (
                    <ItemCategoryBox>
                      <Link to={el.Color_Url}>
                        <ItemColorImg
                          src={el.Color_Img}
                          key={el.i}
                        ></ItemColorImg>
                        <p>{el.Color_Name}</p>
                      </Link>
                    </ItemCategoryBox>
                  );
                })}
              </ItemCategoryContainer>
            </ItemColors>

            <ItemMaterials>
              <ItemInfoCategory>MATERIALS</ItemInfoCategory>
              <ItemCategoryContainer>
                <ItemCategoryBox>
                  <ItemMaterialImg
                    src={product?.Material.Material_Img}
                  ></ItemMaterialImg>
                  <p>{product?.Material.Material_Name}</p>
                </ItemCategoryBox>
              </ItemCategoryContainer>
            </ItemMaterials>
          </ItemInfoBox>
          {/* 가운데 이미지 부분 */}
          <ItemImgBox>
            <MainImgLeft>
              <IoIosArrowBack />
            </MainImgLeft>

            <MainImg src="/Images/model.jpg" />

            <MainImgRight>
              <IoIosArrowForward />
            </MainImgRight>
          </ItemImgBox>
          {/* 상세 페이지 오른쪽 사이즈 주문 부분 */}
          <ItemDetailInfoBox>
            <OrderContainer>
              <ProductMaterials>
                <p>{product?.Material.Material_Name}</p>
              </ProductMaterials>
              <ProductInfo>
                <ProducTitle>
                  <h1>{product?.Title}</h1>
                </ProducTitle>
                <ProductPrice>
                  <span>${product?.Price}</span>
                </ProductPrice>
              </ProductInfo>
              <SizeContainer>
                <SizeGuideBox>
                  <span>SIZE</span>
                  <span>/</span>
                  <span>SIZE GUIDE</span>
                </SizeGuideBox>
                <SizeInfoBox>
                  {product?.ProductSize.map((el, i) => {
                    return <SizeChoice>{el.Size}</SizeChoice>;
                  })}
                </SizeInfoBox>
                <AddBtnBox>
                  <span>ADD TO BAG</span>
                </AddBtnBox>
                <ShippingInfo>
                  <span>FREE SHIPPING ON ORDERS OVER 200 USD.</span>
                </ShippingInfo>
              </SizeContainer>
            </OrderContainer>
            <ProductDetail>
              <IdContainer>
                <ProductDetailBox>
                  <span>ID</span>
                </ProductDetailBox>
                <span>{product?.Serial_Number}</span>
              </IdContainer>
              <DepartmentContainer>
                <ProductDetailBox>
                  <span>DEPARTMENT</span>
                </ProductDetailBox>
                <span>Women</span>
              </DepartmentContainer>
              <TypeContainer>
                <ProductDetailBox>
                  <span>TYPE</span>
                </ProductDetailBox>
                <span>{product?.Type}</span>
              </TypeContainer>
              <ProductContainer>
                <ProductDetailBox>
                  <span>PRODUCT</span>
                </ProductDetailBox>
                <div>
                  <span>{product?.Title}</span>
                </div>
              </ProductContainer>
              {/* 호버 효과 주기 */}
              <MaterialContainer>
                <Link to="/item-detail">
                  <ProductDetailBox>
                    <span>MATERIAL</span>
                  </ProductDetailBox>
                  <span>{product?.Material.Material_Name}</span>
                  <AiOutlinePlusCircle
                    style={{ verticalAlign: "text-top", marginLeft: "3px" }}
                  />
                </Link>
              </MaterialContainer>
              {/* 호버 효과 주기 */}
              <ColorContainer>
                <Link to="/item-detail">
                  <ProductDetailBox>
                    <span>COLOR</span>
                  </ProductDetailBox>
                  <span>Black</span>
                  <AiOutlinePlusCircle
                    style={{ verticalAlign: "text-top", marginLeft: "3px" }}
                  />
                </Link>
              </ColorContainer>
              {/* 호버 효과 주기 */}
              <MadeContainer>
                <Link to="/item-detail">
                  <ProductDetailBox>
                    {/* <span>COLOR</span> */}
                  </ProductDetailBox>
                  <span>MADE IN TURKEY</span>
                  <AiOutlinePlusCircle
                    style={{ verticalAlign: "text-top", marginLeft: "3px" }}
                  />
                </Link>
              </MadeContainer>
            </ProductDetail>
            <ProductDescription>
              <FullDescription>
                <p>
                  Crafted from a slightly stretchy denim twill made of organic
                  cotton, these five-pocket trousers are cut with a mid-rise
                  waist and cropped, kick-flare legs.
                </p>
              </FullDescription>
              <Description>
                <li>- Zip fly with button closure</li>
                <li>- Silver-tone metal hardware</li>
                <li>- Belt loops</li>
                <li>
                  - Organic cotton is cultivated and harvested from
                  non-genetically modified plants, without the use of chemical
                  fertilisers and pesticides.
                </li>
                <li>- Women > Jeans > Cropped</li>
                <li>- Cotton 99%, Elastane 1%</li>
              </Description>
            </ProductDescription>
          </ItemDetailInfoBox>
        </MainContent>
      </Section>
    </>
  );
}

