import React, { useState, useEffect } from "react";
import styled from "styled-components";
import {
  HiOutlinePlusCircle,
  HiOutlineMinusCircle,
  HiOutlineX,
} from "react-icons/hi";

export default function Cart() {
  return (
    <>
      <Nav>
        <span>진희 Nav</span>
      </Nav>
      <Section>
        <CartContainer>
          <h2>Shopping bag</h2>
          <CartItem>
            <CartImgBox>
              <CartImg src="/Images/flower_1.jpg"></CartImg>
            </CartImgBox>
            <CountBox>
              <HiOutlineMinusCircle
                style={{
                  verticalAlign: "text-top",
                  marginLeft: "3px",
                  width: "25px",
                  height: "25px",
                }}
              />
              <span>1</span>
              <HiOutlineX
                style={{
                  verticalAlign: "text-top",
                  marginRight: "5px",
                  width: "22px",
                  height: "22px",
                }}
              />{" "}
              <HiOutlinePlusCircle
                style={{
                  verticalAlign: "text-top",
                  marginLeft: "3px",
                  width: "25px",
                  height: "25px",
                }}
              />
            </CountBox>
            <TitleBox>
              <span>Oversized Wool Hopsack Blazer</span>
            </TitleBox>
            <TitleBox>
              <span>240040-166</span>
            </TitleBox>
            <TitleBox>
              <span>40 Black</span>
            </TitleBox>
            <PriceBox>
              <span>$ 199</span>
            </PriceBox>
            <TotalPriceBox>
              <span>Total $ 199</span>
            </TotalPriceBox>
          </CartItem>
          <CartItem>
            <CartImgBox>
              <CartImg src="/Images/flower_1.jpg"></CartImg>
            </CartImgBox>
            <CountBox>
              <HiOutlineMinusCircle
                style={{
                  verticalAlign: "text-top",
                  marginLeft: "3px",
                  width: "25px",
                  height: "25px",
                }}
              />
              <span>1</span>
              <HiOutlineX
                style={{
                  verticalAlign: "text-top",
                  marginRight: "5px",
                  width: "22px",
                  height: "22px",
                }}
              />{" "}
              <HiOutlinePlusCircle
                style={{
                  verticalAlign: "text-top",
                  marginLeft: "3px",
                  width: "25px",
                  height: "25px",
                }}
              />
            </CountBox>
            <TitleBox>
              <span>Oversized Wool Hopsack Blazer</span>
            </TitleBox>
            <TitleBox>
              <span>240040-166</span>
            </TitleBox>
            <TitleBox>
              <span>40 Black</span>
            </TitleBox>
            <PriceBox>
              <span>$ 199</span>
            </PriceBox>
            <TotalPriceBox>
              <span>Total $ 199</span>
            </TotalPriceBox>
          </CartItem>
          <CartItem>
            <CartImgBox>
              <CartImg src="/Images/flower_1.jpg"></CartImg>
            </CartImgBox>
            <CountBox>
              <HiOutlineMinusCircle
                style={{
                  verticalAlign: "text-top",
                  marginLeft: "3px",
                  width: "25px",
                  height: "25px",
                }}
              />
              <span>1</span>
              <HiOutlineX
                style={{
                  verticalAlign: "text-top",
                  marginRight: "5px",
                  width: "22px",
                  height: "22px",
                }}
              />{" "}
              <HiOutlinePlusCircle
                style={{
                  verticalAlign: "text-top",
                  marginLeft: "3px",
                  width: "25px",
                  height: "25px",
                }}
              />
            </CountBox>
            <TitleBox>
              <span>Oversized Wool Hopsack Blazer</span>
            </TitleBox>
            <TitleBox>
              <span>240040-166</span>
            </TitleBox>
            <TitleBox>
              <span>40 Black</span>
            </TitleBox>
            <PriceBox>
              <span>$ 199</span>
            </PriceBox>
            <TotalPriceBox>
              <span>Total $ 199</span>
            </TotalPriceBox>
          </CartItem>
        </CartContainer>
        <PriceContainer>
          <h2>Bag Total</h2>
          <OrderContainer>
            <OrderPriceBox>
              <span>Order value</span>
              <span>$ 617</span>
            </OrderPriceBox>
            <OrderPriceBox>
              <span>Delivery</span>
              <span>FREE</span>
            </OrderPriceBox>
          </OrderContainer>
        </PriceContainer>
      </Section>
    </>
  );
}

const Nav = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 60px;
  background-color: yellow;
  text-align: center;
  span {
    font-size: 40px;
  }
`;

const Section = styled.section`
  display: flex;
  justify-content: center;
  max-width: 1000px;
  margin: 0 auto;
`;

const CartContainer = styled.div`
  margin-right: 40px;
  width: calc(66.66% - 20px);
  border-bottom: 2px solid black;

  h2 {
    margin-top: 20px;
    padding-bottom: 13px;
    font-size: 20px;
    border-bottom: 2px solid black;
  }
`;

const CartItem = styled.div`
  display: inline-block;
  width: 50%;
  padding: 20px;
  border-bottom: 1px solid black;
  /* border-bottom: 1px solid black; */
`;

const CartImgBox = styled.div`
  display: flex;
  justify-content: center;
  width: 100%;
  padding-bottom: 26px;
`;

const CartImg = styled.img`
  /* max-width: 60px; */
  max-height: 160px;
`;

const CountBox = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  margin-bottom: 10px;

  span {
    margin-left: 15px;
    /* margin-right: 3px; */
    font-size: 20px;
  }
`;

const PriceContainer = styled.div`
  width: calc(33.33% - 20px);
  /* background-color: green; */

  h2 {
    margin-top: 20px;
    padding-bottom: 13px;
    font-size: 20px;
    border-bottom: 2px solid black;
  }
`;

const TitleBox = styled.div`
  display: flex;
  justify-content: center;
  margin: 3px 0px;

  span {
    font-size: 13px;
