import React, { useState, useEffect } from "react";
import styled from "styled-components";
import {
  HiOutlinePlusCircle,
  HiOutlineMinusCircle,
  HiOutlineX,
  HiX,
} from "react-icons/hi";

import { BiXCircle } from "react-icons/bi";

import OrderDescript from "./components/OrderDescript";

export default function Cart() {
  const [cart, setCart] = useState();

  const [totalPrice, setTotalPrice] = useState(0);

  useEffect(() => {
    const fetchData = async () => {
      const result = await fetch(`/Data/cart-Data.json`);
      const json = await result.json();
      setCart(json.data);
    };
    fetchData();
  }, []);

  useEffect(() => {
    // eslint-disable-next-line no-unused-expressions
    const price = cart?.products.reduce((accumulator, item) => {
      return accumulator + item.price * item.count;
    }, 0);
    setTotalPrice(price);
  }, [cart]);

  return (
    <>
      <Nav>
        <span>진희 Nav</span>
      </Nav>
      <Section>
        <CartContainer>
          <h2>Shopping bag</h2>
          {cart?.products.map((item, i) => {
            console.log(cart.products.length);
            return (
              <CartItem
                key={i}
                style={{
                  borderRight: i % 2 === 0 && "1px solid #000",
                  borderBottom:
                    (cart.products.length - 2 === 0 ||
                      cart.products.length - 2 < i) &&
                    "none",
                }}
              >
                <CartImgBox>
                  <span>
                    <BiXCircle
                      style={{
                        verticalAlign: "text-top",
                        marginLeft: "3px",
                        width: "25px",
                        height: "25px",
                        position: "absolute",
                        right: 0,
                        // fill: "white",
                      }}
                    />
                    <HiX
                      style={{
                        verticalAlign: "text-top",
                        marginLeft: "3px",
                        width: "25px",
                        height: "25px",
                        position: "absolute",
                        right: 0,
                        // fill: "white",
                      }}
                    />
                  </span>
                  <CartImg src={item.main_image}></CartImg>
                </CartImgBox>
                <CountBox>
                  <MinusCount />
                  <span>{item.count}</span>
                  <Xcount /> <PlusCount />
                </CountBox>
                <TitleBox>
                  <span>{item.title}</span>
                </TitleBox>
                <TitleBox>
                  <span>{item.serial_num}</span>
                </TitleBox>
                <TitleBox>
                  <span>
                    {item.size} {item.color}
                  </span>
                </TitleBox>
                <PriceBox>
                  <span>$ {item.price}</span>
                </PriceBox>
                <TotalPriceBox>
                  <span>Total $ {item.price * item.count}</span>
                </TotalPriceBox>
              </CartItem>
            );
          })}
        </CartContainer>
        <PriceContainer>
          <h2>Bag Total</h2>
          <OrderContainer>
            <OrderPriceBox>
              <span>Order value</span>
              <span>$ {totalPrice}</span>
            </OrderPriceBox>
            <OrderPriceBox>
              <span>Delivery</span>
              <span>FREE</span>
            </OrderPriceBox>
            <DiscountBox>
              <span>Do you have a discount code?</span>
              <span></span>
            </DiscountBox>
          </OrderContainer>
          <TotalBox>
            <TotalText>TOTAL</TotalText>
            <TotalPrice>${totalPrice}</TotalPrice>
          </TotalBox>
          <AddBtnBox>
            <span>Checkout</span>
          </AddBtnBox>
          <OrderDescript />
        </PriceContainer>
      </Section>
    </>
  );
}

const Nav = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 60px;
  background-color: yellow;
  text-align: center;
  span {
    font-size: 40px;
  }
`;

const Section = styled.section`
  display: flex;
  justify-content: center;
  max-width: 1000px;
  margin: 0 auto;
`;

const CartContainer = styled.div`
