  }
`;

const TotalPriceBox = styled.div`
  display: flex;
  justify-content: center;
  margin: 10px 0px;

  span {
    font-size: 20px;
  }
`;

const OrderContainer = styled.div`
  padding-top: 10px;
`;

const OrderPriceBox = styled.div`
  display: flex;
  justify-content: space-between;
  margin: 10px 0px;

  span {
    font-size: 13px;
  }
`;

const DiscountBox = styled.div`
  display: flex;
  justify-content: space-between;
  margin-top: 10px;
  margin-bottom: 20px;
  opacity: 0.6;
  &:hover {
    opacity: 1;
  }
  span {
    font-size: 13px;
  }
`;

const TotalBox = styled.div`
  display: flex;
  justify-content: space-between;
  margin-top: 18px;
  padding: 16px 0px;
  border-top: 4px solid black;
`;

const TotalText = styled.span`
  font-size: 13px;
`;

const TotalPrice = styled.span`
  font-size: 33px;
`;

const AddBtnBox = styled.button`
  width: 100%;
  height: 50px;
  background-color: black;
  margin-bottom: 15px;
  cursor: pointer;
  span {
    color: white;
  }

  &:hover {
    opacity: 0.8;
  }
`;

const MinusBtn = styled.div`
  cursor: pointer;

  svg {
    color: #cdd1d7;
  }

  &:hover {
    svg {
      fill: black;
      color: white;
    }
  }
`;

const PlusBtn = styled.div`
  cursor: pointer;

  svg {
    color: #cdd1d7;
  }
  &:hover {
    svg {
      fill: black;
      color: white;
