import React, { useState, useEffect } from "react";
import styled, { css } from "styled-components";
import { Link } from "react-router-dom";
import { AiOutlinePlusCircle } from "react-icons/ai";

export default function ItemDetail({ product }) {
  const [MadeStatus, setMadeStatus] = useState(false);

  const onMadeBox = () => {
    setMadeStatus(!MadeStatus);
    console.log(MadeStatus);
  };

  return (
    <ProductDetail>
      <IdContainer>
        <ProductDetailBox>
          <span>ID</span>
        </ProductDetailBox>
        <span>{product?.Serial_Number}</span>
      </IdContainer>
      <DepartmentContainer>
        <ProductDetailBox>
          <span>DEPARTMENT</span>
        </ProductDetailBox>
        <span>Women</span>
      </DepartmentContainer>
      <TypeContainer>
        <ProductDetailBox>
          <span>TYPE</span>
        </ProductDetailBox>
        <span>{product?.Type}</span>
      </TypeContainer>
      <ProductContainer>
        <ProductDetailBox>
          <span>PRODUCT</span>
        </ProductDetailBox>
        <div>
          <span>{product?.Title}</span>
        </div>
      </ProductContainer>
      <MaterialContainer>
        <Link to="/item-detail">
          <ProductDetailBox>
            <span>MATERIAL</span>
          </ProductDetailBox>
          <span>{product?.Material.Material_Name}</span>
          <AiOutlinePlusCircle
            style={{ verticalAlign: "text-top", marginLeft: "3px" }}
          />
        </Link>
      </MaterialContainer>
      <ColorContainer>
        <Link to="/item-detail">
          <ProductDetailBox>
            <span>COLOR</span>
          </ProductDetailBox>
          <span>Black</span>
          <AiOutlinePlusCircle
            style={{ verticalAlign: "text-top", marginLeft: "3px" }}
          />
        </Link>
      </ColorContainer>
      <MadeContainer onClick={onMadeBox}>
        <span>
          <ProductDetailBox />
          <span>MADE IN TURKEY</span>
          <AiOutlinePlusCircle
            style={{ verticalAlign: "text-top", marginLeft: "3px" }}
          />
        </span>
      </MadeContainer>
      {MadeStatus ? (
        <MadeBox>
          <div>
            <ProductDetailBox>MADE IN</ProductDetailBox>
            <span>TURKEY</span>
          </div>
          <div>
            <ProductDetailBox>SUPPLIER</ProductDetailBox>
            <span>Casa Tekstil</span>
          </div>
        </MadeBox>
      ) : (
        ""
      )}

      <CareContainer>
        <Link to="/item-detail">
          <ProductDetailBox />
          <span>CARE INSTRUCTIONS</span>
          <AiOutlinePlusCircle
            style={{ verticalAlign: "text-top", marginLeft: "3px" }}
          />
        </Link>
      </CareContainer>
      <CareBox></CareBox>
    </ProductDetail>
  );
}

const ProductDetail = styled.div`
  margin-top: 15px;
`;

const IdContainer = styled.div`
  padding: 5px 0px;
`;

const ProductDetailBox = styled.div`
  display: inline-block;
  width: 30%;
  text-align: end;
  margin-right: 12px;
`;

const DepartmentContainer = styled.div`
  padding: 5px 0px;
`;

const TypeContainer = styled.div`
  padding: 5px 0px;
`;

const ProductContainer = styled.div`
  display: flex;
