import React, { useState, useEffect } from "react";
import styled, { css } from "styled-components";

export default function ItemDetail({ product }) {
  const [size, setSize] = useState({});

  const [isSoldOut, setIsSoldOut] = useState(false);

  const sizeChoice = (size, soldOut) => {
    setSize(
      {
        size: size,
        soldOut: soldOut,
        choice: false,
      },
      choiceStatus(soldOut)
    );
  };

  const choiceStatus = (soldOut) => {
    if (soldOut) {
      setIsSoldOut(true);
    } else {
      setIsSoldOut(false);
    }
  };

  return (
    <OrderContainer>
      <ProductMaterials>
        <p>{product?.Material.Material_Name}</p>
      </ProductMaterials>
      <ProductInfo>
        <ProducTitle>
          <h1>{product?.Title}</h1>
        </ProducTitle>
        <ProductPrice>
          <span>${product?.Price}</span>
        </ProductPrice>
      </ProductInfo>
      <SizeContainer>
        <SizeGuideBox>
          <span>SIZE</span>
          <span>/</span>
          <span>SIZE GUIDE</span>
        </SizeGuideBox>
        <SizeInfoBox>
          {product?.ProductSize.map((item, i) => {
            return item.soldOut ? (
              <SizeSoldOut onClick={() => sizeChoice(item.size, item.soldOut)}>
                <span>{item.size}</span>
              </SizeSoldOut>
            ) : (
              <SizeChoice
                choice="false"
                onClick={() => sizeChoice(item.size, item.soldOut, { choice })}
                size={size}
              >
                <span>{item.size}</span>
              </SizeChoice>
            );
          })}
        </SizeInfoBox>
        {isSoldOut ? (
          <DisalbedBtnBox>
            <span>ADD TO BAG</span>
          </DisalbedBtnBox>
        ) : (
          <AddBtnBox>
            <span>ADD TO BAG</span>
          </AddBtnBox>
        )}

        <ShippingInfo>
          <span>FREE SHIPPING ON ORDERS OVER 200 USD.</span>
        </ShippingInfo>
      </SizeContainer>
    </OrderContainer>
  );
}

const OrderContainer = styled.div``;

const ProductMaterials = styled.div`
  p {
    font-size: 13px;
    padding-bottom: 10px;
  }
`;

const ProductInfo = styled.div`
  display: flex;
  justify-content: space-between;
  padding-bottom: 18px;
  border-bottom: 1px solid #cdd1d7;
`;

const ProducTitle = styled.div`
  display: inline-block;
  h1 {
    max-width: 224px;
    font-size: 20px;
    margin-right: 5px;
  }
`;

const ProductPrice = styled.div`
  display: inline-block;

  span {
    font-size: 20px;
  }
`;

const SizeContainer = styled.div``;

const SizeGuideBox = styled.div`
  padding-top: 10px;
  padding-bottom: 8px;
  span {
    font-size: 13px;
  }
`;

const SizeInfoBox = styled.div`
  margin-top: 8px;
  margin-bottom: 4px;
`;

const SizeChoice = styled.button`
  display: inline-block;
  width: 40px;
  height: 40px;
  margin-right: 8px;
  margin-bottom: 14px;
  border: 1px solid #000;
  border-radius: 50%;
  cursor: pointer;

  /* {size.size === {item.size}  &&
 background-color: black;
  color: white; } */

  background-color: ${(props) =>
    //     props.size === props.item.size ? "black" : ""};
    //   color: white;
    console.log(props.size)};
`;

const SizeSoldOut = styled.button`
  display: inline-block;
  width: 40px;
  height: 40px;
  margin-right: 8px;
  margin-bottom: 14px;
  border: 1px solid #979797;
  border-radius: 50%;
  background: linear-gradient(
    to top right,
    rgba(151, 151, 151, 0) 0,
    rgba(151, 151, 151, 0) calc(50% - 0.8px),
    #979797 50%,
    rgba(151, 151, 151, 0) calc(50% + 0.8px),
    rgba(151, 151, 151, 0) 100%
  );

  span {
    color: #979797;
  }
`;

const AddBtnBox = styled.button`
  width: 100%;
  height: 50px;
  background-color: black;
  margin-bottom: 15px;
  cursor: pointer;
  span {
    color: white;
  }

  &:hover {
    opacity: 0.8;
  }
`;

const DisalbedBtnBox = styled.button`
  width: 100%;
  height: 50px;
  background-color: gray;
  margin-bottom: 15px;
  opacity: 0.7;
  cursor: pointer;
  span {
    color: white;
  }

  &:hover {
    opacity: 0.5;
  }
`;

const ShippingInfo = styled.div`
  text-align: center;
  padding-bottom: 15px;
  border-bottom: 1px solid #000;
  span {
    font-size: 12px;
  }
`;
