import React, { useState, useEffect } from "react";
import styled from "styled-components";
import {
  HiOutlinePlusCircle,
  HiOutlineMinusCircle,
  HiOutlineX,
} from "react-icons/hi";

export default function Cart() {
  const [cart, setCart] = useState();

  useEffect(() => {
    const fetchData = async () => {
      const result = await fetch(`/Data/cart-Data.json`);
      const json = await result.json();
      setCart(json.data);
    };
    fetchData();
  }, []);

  console.log(cart);
  return (
    <>
      <Nav>
        <span>진희 Nav</span>
      </Nav>
      <Section>
        <CartContainer>
          <h2>Shopping bag</h2>
          {cart.map((item) => {
            return (
              <CartItem>
                <CartImgBox>
                  <CartImg src="/Images/flower_1.jpg"></CartImg>
                </CartImgBox>
                <CountBox>
                  <HiOutlineMinusCircle
                    style={{
                      verticalAlign: "text-top",
                      marginLeft: "3px",
                      width: "25px",
                      height: "25px",
                    }}
                  />
                  <span>{cart.product.count}</span>
                  <HiOutlineX
                    style={{
                      verticalAlign: "text-top",
                      marginRight: "5px",
                      width: "22px",
                      height: "22px",
                    }}
                  />{" "}
                  <HiOutlinePlusCircle
                    style={{
                      verticalAlign: "text-top",
                      marginLeft: "3px",
                      width: "25px",
                      height: "25px",
                    }}
                  />
                </CountBox>
                <TitleBox>
                  <span>Oversized Wool Hopsack Blazer</span>
                </TitleBox>
                <TitleBox>
                  <span>240040-166</span>
                </TitleBox>
                <TitleBox>
                  <span>40 Black</span>
                </TitleBox>
                <PriceBox>
                  <span>$ 199</span>
                </PriceBox>
                <TotalPriceBox>
                  <span>Total $ 199</span>
                </TotalPriceBox>
              </CartItem>
            );
          })}
        </CartContainer>
        <PriceContainer>
          <h2>Bag Total</h2>
          <OrderContainer>
            <OrderPriceBox>
              <span>Order value</span>
              <span>$ 617</span>
            </OrderPriceBox>
            <OrderPriceBox>
              <span>Delivery</span>
              <span>FREE</span>
            </OrderPriceBox>
            <DiscountBox>
              <span>Do you have a discount code?</span>
              <span></span>
            </DiscountBox>
          </OrderContainer>
          <TotalBox>
            <TotalText>TOTAL</TotalText>
            <TotalPrice>$ 617</TotalPrice>
          </TotalBox>
          <AddBtnBox>
            <span>Checkout</span>
          </AddBtnBox>
          <OrderDescript>
            <div>
              <span>Read more about how to return an item here</span>
              <span>Free shipping on orders over 200 USD*</span>
              <span>Read more about delivery options here</span>
            </div>
            <div>
              <span>Secure payment</span>
              <span>Cards, Klarna & PayPal. Read more about Payment.</span>
              <span>
                In order to use the PayPal option, pop-ups need to be enabled in
                your browser settings.
              </span>
            </div>
            <div>
              <span>Do you need help?</span>
              <span>Go to Customer service</span>
              <span>
                ARKET will process your personal data to manage your order, in
                accordance with ARKET Privacy Notice
              </span>
            </div>
          </OrderDescript>
        </PriceContainer>
      </Section>
    </>
  );
}

const Nav = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 60px;
  background-color: yellow;
  text-align: center;
  span {
    font-size: 40px;
  }
`;

