  }
`;

const OrderContainer = styled.div`
  padding-top: 10px;
`;

const OrderPriceBox = styled.div`
  display: flex;
  justify-content: space-between;
  margin: 10px 0px;

  span {
    font-size: 13px;
  }
`;

const DiscountBox = styled.div`
  display: flex;
  justify-content: space-between;
  margin-top: 10px;
  margin-bottom: 20px;
  opacity: 0.6;
  &:hover {
    opacity: 1;
