  }
`;

const OrderDescript = styled.div`
  margin-top: 10px;
  padding-top: 20px;
  border-top: 4px solid black;

  div {
    margin-bottom: 50px;
    span {
      display: block;
      font-size: 14px;
      opacity: 0.6;
      margin-bottom: 23px;
    }
  }
`;
