import React, { useState, useEffect } from "react";
import styled, { css } from "styled-components";
import { Link } from "react-router-dom";
import { AiOutlinePlusCircle } from "react-icons/ai";

export default function ItemDetail(props) {
  return (
    <ProductDetail>
      <IdContainer>
        <ProductDetailBox>
          <span>ID</span>
        </ProductDetailBox>
        <span>{product?.Serial_Number}</span>
      </IdContainer>
      <DepartmentContainer>
        <ProductDetailBox>
          <span>DEPARTMENT</span>
        </ProductDetailBox>
        <span>Women</span>
      </DepartmentContainer>
      <TypeContainer>
        <ProductDetailBox>
          <span>TYPE</span>
        </ProductDetailBox>
        <span>{product?.Type}</span>
      </TypeContainer>
      <ProductContainer>
        <ProductDetailBox>
          <span>PRODUCT</span>
        </ProductDetailBox>
        <div>
          <span>{product?.Title}</span>
        </div>
      </ProductContainer>
      {/* 호버 효과 주기 */}
      <MaterialContainer>
        <Link to="/item-detail">
          <ProductDetailBox>
            <span>MATERIAL</span>
          </ProductDetailBox>
          <span>{product?.Material.Material_Name}</span>
          <AiOutlinePlusCircle
            style={{ verticalAlign: "text-top", marginLeft: "3px" }}
          />
        </Link>
      </MaterialContainer>
      {/* 호버 효과 주기 */}
      <ColorContainer>
        <Link to="/item-detail">
          <ProductDetailBox>
            <span>COLOR</span>
          </ProductDetailBox>
          <span>Black</span>
          <AiOutlinePlusCircle
            style={{ verticalAlign: "text-top", marginLeft: "3px" }}
          />
        </Link>
      </ColorContainer>
      {/* 호버 효과 주기 */}
      <MadeContainer>
        <Link to="/item-detail">
          <ProductDetailBox>{/* <span>COLOR</span> */}</ProductDetailBox>
          <span>MADE IN TURKEY</span>
          <AiOutlinePlusCircle
            style={{ verticalAlign: "text-top", marginLeft: "3px" }}
          />
        </Link>
      </MadeContainer>
    </ProductDetail>
  );
}

const ProductDetail = styled.div`
  margin-top: 15px;
`;

const IdContainer = styled.div`
  padding: 5px 0px;
`;

const ProductDetailBox = styled.div`
  display: inline-block;
  width: 30%;
  text-align: end;
  margin-right: 12px;
`;

const DepartmentContainer = styled.div`
  padding: 5px 0px;
`;

const TypeContainer = styled.div`
  padding: 5px 0px;
`;

const ProductContainer = styled.div`
  display: flex;
  padding: 5px 0px;
  div {
    max-width: 68%;
  }
`;

const MaterialContainer = styled.div`
  padding: 5px 0px;

  &:hover {
    opacity: 0.5;
  }
`;

const ColorContainer = styled.div`
  padding: 5px 0px;

  &:hover {
    opacity: 0.5;
  }
`;

const MadeContainer = styled.div`
  padding: 5px 0px;

  &:hover {
    opacity: 0.5;
  }
`;
