import React, { useState, useEffect } from "react";
import styled from "styled-components";
import {
  HiOutlinePlusCircle,
  HiOutlineMinusCircle,
  HiOutlineX,
} from "react-icons/hi";

import OrderDescript from "./components/OrderDescript";

export default function Cart() {
  const [cart, setCart] = useState();

  const [totalPrice, setTotalPrice] = useState(0);

  useEffect(() => {
    const fetchData = async () => {
      const result = await fetch(`/Data/cart-Data.json`);
      const json = await result.json();
      setCart(json.data);
    };
    fetchData();
  }, []);

  // useEffect(() => {
  //   const pricer = () => {
  //     cart?.products.map((el) => console.log(el.price));
  //   };
  // });

  return (
    <>
      <Nav>
        <span>진희 Nav</span>
      </Nav>
      <Section>
        <CartContainer>
          <h2>Shopping bag</h2>
          {cart?.products.map((item, i) => {
            return (
              <CartItem
                key={i}
                style={{
                  borderRight: i % 2 === 0 && "1px solid #000",
                  borderBottom: cart.products.length - 2 < i && "none",
                }}
              >
                <CartImgBox>
                  <CartImg src={item.main_image}></CartImg>
                </CartImgBox>
                <CountBox>
                  <MinusCount />
                  <span>{item.count}</span>
                  <Xcount /> <PlusCount />
                </CountBox>
                <TitleBox>
                  <span>{item.title}</span>
                </TitleBox>
                <TitleBox>
                  <span>{item.serial_num}</span>
                </TitleBox>
                <TitleBox>
                  <span>
                    {item.size} {item.color}
                  </span>
                </TitleBox>
                <PriceBox>
                  <span>$ {item.price}</span>
                </PriceBox>
                <TotalPriceBox>
                  <span>Total $ {item.price * item.count}</span>
                </TotalPriceBox>
              </CartItem>
            );
          })}
        </CartContainer>
        <PriceContainer>
          <h2>Bag Total</h2>
          <OrderContainer>
            <OrderPriceBox>
              <span>Order value</span>
              <span>$ 617</span>
            </OrderPriceBox>
            <OrderPriceBox>
              <span>Delivery</span>
              <span>FREE</span>
            </OrderPriceBox>
            <DiscountBox>
              <span>Do you have a discount code?</span>
              <span></span>
            </DiscountBox>
          </OrderContainer>
          <TotalBox>
            <TotalText>TOTAL</TotalText>
            <TotalPrice>${totalPrice}</TotalPrice>
          </TotalBox>
          <AddBtnBox>
            <span>Checkout</span>
          </AddBtnBox>
          <OrderDescript />
        </PriceContainer>
      </Section>
    </>
  );
}

const Nav = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 60px;
  background-color: yellow;
  text-align: center;
  span {
    font-size: 40px;
  }
`;

const Section = styled.section`
  display: flex;
  justify-content: center;
  max-width: 1000px;
  margin: 0 auto;
`;

const CartContainer = styled.div`
  margin-right: 40px;
  width: calc(66.66% - 20px);
  border-bottom: 2px solid black;
  margin-bottom: 100px;

  h2 {
    margin-top: 20px;
    padding-bottom: 13px;
    font-size: 20px;
    border-bottom: 2px solid black;
  }
`;

const CartItem = styled.div`
  display: inline-block;
  width: 50%;
  padding: 20px;
  border-bottom: 1px solid black;
  /* border-bottom: 1px solid black; */
  svg {
    color: #cdd1d7;
  }

  &:hover {
    svg {
      color: #000;
    }
  }
`;

const CartImgBox = styled.div`
  display: flex;
  justify-content: center;
  width: 100%;
  padding-bottom: 26px;
`;

const CartImg = styled.img`
  max-height: 160px;
`;

const CountBox = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  margin-bottom: 10px;

  span {
    margin-left: 15px;
    font-size: 20px;
  }
`;

const PriceContainer = styled.div`
  width: calc(33.33% - 20px);
  /* background-color: green; */

  h2 {
    margin-top: 20px;
    padding-bottom: 13px;
    font-size: 20px;
    border-bottom: 2px solid black;
  }
`;

const TitleBox = styled.div`
  display: flex;
  justify-content: center;
  margin: 3px 0px;

  span {
    font-size: 13px;
  }
`;

const PriceBox = styled.div`
  display: flex;
  justify-content: center;
  margin: 10px 0px;

  span {
    font-size: 13px;
  }
`;

const TotalPriceBox = styled.div`
  display: flex;
  justify-content: center;
  margin: 10px 0px;

  span {
    font-size: 20px;
  }
`;

const OrderContainer = styled.div`
  padding-top: 10px;
`;

const OrderPriceBox = styled.div`
  display: flex;
  justify-content: space-between;
  margin: 10px 0px;

  span {
    font-size: 13px;
  }
`;

const DiscountBox = styled.div`
  display: flex;
  justify-content: space-between;
  margin-top: 10px;
  margin-bottom: 20px;
  opacity: 0.6;
  &:hover {
    opacity: 1;
  }
  span {
    font-size: 13px;
  }
`;

const TotalBox = styled.div`
  display: flex;
  justify-content: space-between;
  margin-top: 18px;
  padding: 16px 0px;
  /* margin-bottom: 20px; */
  border-top: 4px solid black;
`;

const TotalText = styled.span`
  font-size: 13px;
`;

const TotalPrice = styled.span`
  font-size: 33px;
`;

const AddBtnBox = styled.button`
  width: 100%;
  height: 50px;
  background-color: black;
  margin-bottom: 15px;
  cursor: pointer;
  span {
    color: white;
  }

  &:hover {
    opacity: 0.8;
  }
`;

const MinusBtn = styled.div`
  cursor: pointer;

  svg {
    color: #cdd1d7;
  }

  &:hover {
    svg {
      fill: black;
      color: white;
    }
  }
`;

const PlusBtn = styled.div`
  cursor: pointer;

  svg {
    color: #cdd1d7;
  }
  &:hover {
    svg {
      fill: black;
      color: white;
      xmlns: "http://www.w3.org/2000/svg";
      viewbox: "0 0 500 500";
      width: 50px;
      height: 50px;
    }
  }
`;

function MinusCount() {
  return (
    <MinusBtn>
      <HiOutlineMinusCircle
        style={{
          verticalAlign: "text-top",
          marginLeft: "3px",
          width: "25px",
          height: "25px",
        }}
      />
    </MinusBtn>
  );
}

function PlusCount() {
  return (
    <PlusBtn>
      <HiOutlinePlusCircle
        style={{
          verticalAlign: "text-top",
          marginLeft: "3px",
          width: "25px",
          height: "25px",
        }}
      />
    </PlusBtn>
  );
}

function Xcount() {
  return (
    <HiOutlineX
      style={{
        verticalAlign: "text-top",
        marginRight: "5px",
        width: "22px",
        height: "22px",
        color: "#000",
      }}
    />
  );
}
