import React, { useEffect } from "react";
import styled from "styled-components";
import {useDispatch, useSelector} from 'react-redux'
import CartItem from "./components/CartItem";
import PriceContainer from "./components/PriceContainer";
import Nav from "../../components/Nav";
import { API } from "../../config";
import {getItems, setTotal, increaseCount, decreaseCount, removeItem} from '../../modules/cart';

export default function Cart() {
  const dispatch = useDispatch();
  const {items, totalPrice} = useSelector(({cart}) => ({
    items: cart.items,
    totalPrice: cart.totalPrice
  }))

  useEffect(() => {
    fetch(`${API}/cart`, {
      headers: { Authorization: localStorage.getItem("token") },
    })
    .then((res) => res.json())
    .then(({data}) => dispatch(getItems(data)))
  }, [dispatch]);

  useEffect(() => {
    dispatch(setTotal())
  }, [dispatch, items])

  const deleteCart = (idx) => {
    const cartList = [...items];
    const updateItem = cartList.filter(
      (item) => item.cart_id !== items[idx].cart_id
    );
    dispatch(removeItem(updateItem));
  };

  const fetchRemove = async (idx) => {
    try {
      const deleteResult = await fetch(`${API}/cart`, {
        method: "delete",
        headers: { Authorization: localStorage.getItem("token") },
        body: JSON.stringify({
          cart_id: items[idx].cart_id,
        }),
      });
      const { message } = await deleteResult.json();

      if (message === "SUCCESS") {
        deleteCart(idx);
      }
    } catch (err) {
      console.log(err);
    }
  };

  const fetchCount = async (idx, change, cartList) => {
    try {
      const patchResult = await fetch(`${API}/cart`, {
        method: "patch",
        headers: { Authorization: localStorage.getItem("token") },
        body: JSON.stringify({
          cart_id: items[idx].cart_id,
          count: cartList[idx].count,
        }),
      });
      const { message } = await patchResult.json();

      if (message === "SUCCESS") {
        if (change === 1) {
          dispatch(increaseCount(cartList))
        } else {
          dispatch(decreaseCount(cartList))
        }
      }
    } catch (err) {
      console.log(err);
    }
  };

  const changeCount = (idx, change) => {
    const cartList = [...items];
    const updateItem = {
      ...items[idx],
      count: items[idx].count + change,
    };
    cartList[idx] = updateItem;

    if (cartList[idx].count === 0 && change === -1) {
      fetchRemove(idx);
    } else {
      fetchCount(idx, change, cartList);
    }
  };

  return (
    <>
      <Nav />
      <Section>
        <CartContainer>
          <h2>Shopping bag</h2>
          {items?.map((item, idx) => {
            return (
              <CartItem
                key={idx}
                cart={items}
                idx={idx}
                item={item}
                changeCount={changeCount}
                deleteCart={deleteCart}
                fetchRemove={fetchRemove}
              />
            );
          })}
        </CartContainer>
        <PriceContainer totalPrice={totalPrice} />
      </Section>
    </>
  );
}

const Section = styled.section`
  display: flex;
  justify-content: center;
  max-width: 1000px;
  margin: 0 auto;
`;

const CartContainer = styled.div`
  margin-right: 40px;
  width: calc(66.66% - 20px);
  border-bottom: 2px solid black;
  margin-bottom: 100px;
  height: 100%;

  h2 {
    margin-top: 20px;
    padding-bottom: 13px;
    font-size: 20px;
    border-bottom: 2px solid black;
  }
`;
