import React from "react";
import styled from "styled-components";
import {
    HiOutlinePlusCircle,
    HiOutlineMinusCircle,
    HiOutlineX,
  } from "react-icons/hi";

export default function CartItems({cart, item, i,decreaseCount ,increaseCount, deleteCart}) {
  return (
    <CartItem
    key={i}
    style={{
      borderRight: i % 2 === 0 && "1px solid #000",
      borderBottom:
        (cart.products.length - 2 === 0 ||
          cart.products.length - 2 < i) &&
        "none",
    }}
    count={item.count}
  >
    <CartImgBox>
      <span>
        <RemoveBox onClick={() =>deleteCart(i)}>
          <span>X</span>
        </RemoveBox>
      </span>
      <CartImg src={item.main_image}></CartImg>
    </CartImgBox>
    <CountBox>
      <Minus onClick={() => decreaseCount(i)} count={item.count}>
        <MinusText>-</MinusText>
      </Minus>
      {/* <MinusCount decreaseCount={() => decreaseCount(i)} count={item.count}/> */}
      <CountX>
        <CountText>{item.count}</CountText>
        <Xcount /> 
      </CountX>
      {/* <PlusCount increaseCount={() => increaseCount(i)} count={item.count}/> */}
      <Plus onClick={() => increaseCount(i)} count={item.count}>
        <PlusText>+</PlusText>
      </Plus>
    </CountBox>
    <TitleBox>
      <span>{item.title}</span>
    </TitleBox>
    <TitleBox>
      <span>{item.serial_num}</span>
    </TitleBox>
    <TitleBox>
      <span>
        {item.size} {item.color}
      </span>
    </TitleBox>
    <TitleBox>
      <span>$ {item.price}</span>
    </TitleBox>
    <TotalPriceBox>
      <span>Total $ {item.price * item.count}</span>
    </TotalPriceBox>
  </CartItem>
  );
}

const CartItem = styled.div`
${props => console.log(props.count)}
  display: inline-block;
  width: 50%;
  padding: 20px;
  border-bottom: 1px solid black;

  div {
    span {
      div {
        color: white;
        span {
          font-size: 20px;
        }
      }
    }
  }

  &:hover {
    div {
      span {
        div {
          color: black;
        }
      }
    }
  }
`;

const RemoveBox = styled.div`
  position: absolute;
  right: 0;
  border-radius: 100%;
  width: 24px;
  height: 24px;
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;

  span {
    margin-top: 4px;
    font-weight: bold;
  }

  &:hover {
    border: 2px solid #000;
    color: #000;
  }
`;

const CartImgBox = styled.div`
  display: flex;
  justify-content: center;
  width: 100%;
  position: relative;
  padding-bottom: 26px;
`;

const CartImg = styled.img`
  max-height: 160px;
  cursor: pointer;
`;

const CountBox = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  margin-bottom: 10px;
`;

const CountText = styled.span`
  margin-left: 15px;
  font-size: 20px;
`;

const TitleBox = styled.div`
  display: flex;
  justify-content: center;
  margin: 3px 0px;

  span {
    font-size: 13px;
  }
`;

const PriceBox = styled.div`
  display: flex;
  justify-content: center;
  margin: 10px 0px;

  span {
    font-size: 13px;
  }
`;

const TotalPriceBox = styled(PriceBox)`
  span {
    font-size: 20px;
  }
`;


