import React, { useState } from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import { AiOutlinePlusCircle } from "react-icons/ai";
import {
  FcSupport,
  FcShare,
  FcPaid,
  FcMissedCall,
  FcMiddleBattery,
} from "react-icons/fc";

export default function ItemDetail({ product }) {
  const [madeStatus, setMadeStatus] = useState(true);

  const [careStatus, setCareStatus] = useState(true);

  const onMadeBox = () => {
    if (madeStatus) {
      setMadeStatus(false);
      setCareStatus(true);
    } else {
      setMadeStatus(true);
      setCareStatus(true);
    }
  };

  const onCareBox = () => {
    if (careStatus) {
      setMadeStatus(true);
      setCareStatus(false);
    } else {
      setMadeStatus(true);
      setCareStatus(true);
    }
  };

  return (
    <ProductDetail>
      <IdContainer>
        <ProductDetailBox>
          <span>ID</span>
        </ProductDetailBox>
        <span>{product?.product.Serial_Number}</span>
      </IdContainer>
      <DepartmentContainer>
        <ProductDetailBox>
          <span>DEPARTMENT</span>
        </ProductDetailBox>
        <span>Women</span>e
      </DepartmentContainer>
      <DepartmentContainer>
        <ProductDetailBox>
          <span>TYPE</span>
        </ProductDetailBox>
        <span>{product?.product.Type}</span>
      </DepartmentContainer>
      <ProductContainer>
        <ProductDetailBox>
          <span>PRODUCT</span>
        </ProductDetailBox>
        <div>
          <span>{product?.product.Title}</span>
        </div>
      </ProductContainer>
      <MaterialContainer>
        <Link to="/item-detail">
          <ProductDetailBox>
            <span>MATERIAL</span>
          </ProductDetailBox>
          <span>{product?.product.Material.Material_Name}</span>
          <PlusIcon />
        </Link>
      </MaterialContainer>
      <ColorContainer>
        <Link to="/item-detail">
          <ProductDetailBox>
            <span>COLOR</span>
          </ProductDetailBox>
          <span>Black</span>
          <PlusIcon />
        </Link>
      </ColorContainer>
      <MadeContainer onClick={onMadeBox}>
        <span>
          <ProductDetailBox />
          <span>MADE IN TURKEY</span>
          <PlusIcon />
        </span>
        <MadeBox
          style={{
            height: madeStatus ? "0px" : "67px",
            margin: madeStatus && "0px",
            padding: madeStatus && "0px",
            border: madeStatus && "0px",
          }}
        >
          <div>
            <ProductDetailBox>MADE IN</ProductDetailBox>
            <span>TURKEY</span>
          </div>
          <div>
            <ProductDetailBox>SUPPLIER</ProductDetailBox>
            <span>Casa Tekstil</span>
          </div>
        </MadeBox>
      </MadeContainer>

      <CareContainer onClick={onCareBox}>
        <Link to="/item-detail">
          <ProductDetailBox />
          <span>CARE INSTRUCTIONS</span>
          <PlusIcon />
        </Link>
        <CareBox
          style={{
            height: careStatus ? "0px" : "100px",
            margin: careStatus && "0px",
            padding: careStatus && "0px",
            border: careStatus && "0px",
          }}
        >
          <div>
            <FcSupport />
            <FcShare />
            <FcPaid />
            <FcMissedCall />
            <FcMiddleBattery />
          </div>
          <span>
            Line dry, Iron at low temperature, Only non-chlorine bleach, Wash at
            or below 40°C, Dry clean any solvent except tetrachloroethylene.
          </span>
        </CareBox>
      </CareContainer>
    </ProductDetail>
  );
}

const ProductDetail = styled.div`
  margin-top: 15px;
  transition-timing-function: width 3s ease-in-out;
`;

const IdContainer = styled.div`
  padding: 5px 0px;
`;

const ProductDetailBox = styled.div`
  display: inline-block;
  width: 30%;
  text-align: end;
  margin-right: 12px;
`;

const DepartmentContainer = styled.div`
  padding: 5px 0px;
`;

const ProductContainer = styled.div`
  display: flex;
  padding: 5px 0px;
  div {
    max-width: 68%;
  }
`;

const MaterialContainer = styled.div`
  padding: 5px 0px;

  &:hover {
    opacity: 0.5;
  }
`;

const ColorContainer = styled.div`
  padding: 5px 0px;

  &:hover {
    opacity: 0.5;
  }
`;

const MadeContainer = styled.div`
  padding: 5px 0px;
  cursor: pointer;

  span {
    &:hover {
      opacity: 0.5;
    }
  }
`;

const MadeBox = styled.div`
  margin: 10px 0px;
  border-top: 1px solid #000;
  border-bottom: 1px solid #000;
  opacity: 1;
  transition-duration: 0.5s;
  transition-delay: 0.2s;
  transition-timing-function: ease-in-out;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;

  div {
    margin: 5px 0px;

    span {
      margin-left: 15px;
      font-size: 15px;
    }
  }
`;

const CareContainer = styled.div`
  padding: 5px 0px;

  &:hover {
    opacity: 0.5;
  }
`;

const CareBox = styled.div`
  margin: 10px 0px;
  border-top: 1px solid #000;
  border-bottom: 1px solid #000;
  opacity: 1;
  transition-duration: 0.5s;
  transition-delay: 0.2s;
  transition-timing-function: ease-in-out;
  /* white-space: nowrap; */
  overflow: hidden;
  /* text-overflow: ellipsis; */

  div {
    display: flex;
    justify-content: space-evenly;
    margin: 20px 0px;

    svg {
      width: 30px;
      height: 30px;
    }
  }

  span {
    margin-top: 20px;
    font-size: 13px;
  }
`;

function PlusIcon() {
  return (
    <AiOutlinePlusCircle
      style={{ verticalAlign: "text-top", marginLeft: "3px" }}
    />
  );
}
