import React, { useState, useEffect } from "react";
import styled from "styled-components";
import {
  HiOutlinePlusCircle,
  HiOutlineMinusCircle,
  HiOutlineX,
  HiX,
} from "react-icons/hi";

import { BiXCircle } from "react-icons/bi";

import OrderDescript from "./components/OrderDescript";

export default function Cart() {
  const [cart, setCart] = useState();

  const [totalPrice, setTotalPrice] = useState(0);

  useEffect(() => {
    const fetchData = async () => {
      const result = await fetch(`/Data/cart-Data.json`);
      const json = await result.json();
      setCart(json.data);
    };
    fetchData();
  }, []);

  useEffect(() => {
    // eslint-disable-next-line no-unused-expressions
    const price = cart?.products.reduce((accumulator, item) => {
      return accumulator + item.price * item.count;
    }, 0);
    setTotalPrice(price);
  }, [cart]);

  const decreaseCount = (i) => {
    const carts = {...cart};
    const updateCart = {
      "id": cart.products[i].id,
      "title": cart.products[i].title,
      "count": cart.products[i].count -1,
      "price": cart.products[i].price,
      "size": cart.products[i].size,
      "color": cart.products[i].color,
      "serial_num": cart.products[i].serial_num,
      "main_image": cart.products[i].main_image
    }
    carts.products[i] = updateCart
    setCart(carts)
  }

  const increaseCount = (i) => {
    const carts = {...cart};
    const updateCart = {
      "id": cart.products[i].id,
      "title": cart.products[i].title,
      "count": cart.products[i].count +1,
      "price": cart.products[i].price,
      "size": cart.products[i].size,
      "color": cart.products[i].color,
      "serial_num": cart.products[i].serial_num,
      "main_image": cart.products[i].main_image
    }
    carts.products[i] = updateCart
    setCart(carts)
  }

  return (
    <>
      <Nav>
        <span>진희 Nav</span>
      </Nav>
      <Section>
        <CartContainer>
          <h2>Shopping bag</h2>
          {cart?.products.map((item, i) => {
            return (
              <CartItem
                key={i}
                style={{
                  borderRight: i % 2 === 0 && "1px solid #000",
                  borderBottom:
                    (cart.products.length - 2 === 0 ||
                      cart.products.length - 2 < i) &&
                    "none",
                }}
              >
                <CartImgBox>
                  <span>
                    <Xbox>
                      <span>X</span>
                    </Xbox>
                  </span>
                  <CartImg src={item.main_image}></CartImg>
                </CartImgBox>
                <CountBox>
                  <MinusCount decreaseCount={() => decreaseCount(i)}/>
                  <span>{item.count}</span>
                  <Xcount /> 
                  <PlusCount increaseCount={() => increaseCount(i)} />
                </CountBox>
                <TitleBox>
                  <span>{item.title}</span>
                </TitleBox>
                <TitleBox>
                  <span>{item.serial_num}</span>
                </TitleBox>
                <TitleBox>
                  <span>
                    {item.size} {item.color}
                  </span>
                </TitleBox>
                <PriceBox>
                  <span>$ {item.price}</span>
                </PriceBox>
                <TotalPriceBox>
                  <span>Total $ {item.price * item.count}</span>
                </TotalPriceBox>
              </CartItem>
            );
          })}
        </CartContainer>
        <PriceContainer>
          <h2>Bag Total</h2>
          <OrderContainer>
            <OrderPriceBox>
              <span>Order value</span>
              <span>$ {totalPrice}</span>
            </OrderPriceBox>
            <OrderPriceBox>
              <span>Delivery</span>
              <span>FREE</span>
            </OrderPriceBox>
            <DiscountBox>
              <span>Do you have a discount code?</span>
              <span></span>
            </DiscountBox>
          </OrderContainer>
          <TotalBox>
            <TotalText>TOTAL</TotalText>
            <TotalPrice>${totalPrice}</TotalPrice>
          </TotalBox>
          <AddBtnBox>
            <span>Checkout</span>
          </AddBtnBox>
          <OrderDescript />
        </PriceContainer>
      </Section>
    </>
  );
}

const Nav = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 60px;
  background-color: yellow;
  text-align: center;
  span {
    font-size: 40px;
  }
`;

const Section = styled.section`
  display: flex;
  justify-content: center;
  max-width: 1000px;
  margin: 0 auto;
`;

const CartContainer = styled.div`
  margin-right: 40px;
  width: calc(66.66% - 20px);
  border-bottom: 2px solid black;
  margin-bottom: 100px;
  height: 100%;
  h2 {
    margin-top: 20px;
    padding-bottom: 13px;
    font-size: 20px;
    border-bottom: 2px solid black;
  }
`;

const CartItem = styled.div`
  display: inline-block;
  width: 50%;
  padding: 20px;
  border-bottom: 1px solid black;

  div {
    span {
      div {
        color: white;
        span {
          font-size: 20px;
        }
      }
    }
  }
  &:hover {
    div {
      span {
        div {
          color: black;
        }
      }
    }
  }

  svg {
    color: #cdd1d7;
  }

  &:hover {
    svg {
      color: #000;
    }
  }
`;

const Xbox = styled.div`
  position: absolute;
  right: 0;
  /* color: white; */
  border-radius: 100%;
  width: 24px;
  height: 24px;
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;

  span {
    margin-top: 4px;
    font-weight: bold;
  }
  &:hover {
    border: 2px solid #000;
    color: #000;
  }
`;

const CartImgBox = styled.div`
  display: flex;
  justify-content: center;
  width: 100%;
  position: relative;
  padding-bottom: 26px;
`;

const CartImg = styled.img`
  max-height: 160px;
  cursor: pointer;
`;

const CountBox = styled.div`
  display: flex;
