import React from "react";
import styled from "styled-components";
import {
    HiOutlinePlusCircle,
    HiOutlineMinusCircle,
    HiOutlineX,
  } from "react-icons/hi";

export default function CartItems({cart, item, i,decreaseCount ,increaseCount, deleteCart}) {
  return (
    <CartItem
    key={i}
    style={{
      borderRight: i % 2 === 0 && "1px solid #000",
      borderBottom:
        (cart.products.length - 2 === 0 ||
          cart.products.length - 2 < i) &&
        "none",
    }}
    count={item.count}
  >
    <CartImgBox>
      <span>
        <Xbox onClick={() =>deleteCart(i)}>
          <span>X</span>
        </Xbox>
      </span>
      <CartImg src={item.main_image}></CartImg>
    </CartImgBox>
    <CountBox>
      <MinusCount decreaseCount={() => decreaseCount(i)} count={item.count}/>
      <span>{item.count}</span>
      <Xcount /> 
      <span>
        <PlusCount increaseCount={() => increaseCount(i)} count={item.count}/>
      </span>
    </CountBox>
    <TitleBox>
      <span>{item.title}</span>
    </TitleBox>
    <TitleBox>
      <span>{item.serial_num}</span>
    </TitleBox>
    <TitleBox>
      <span>
        {item.size} {item.color}
      </span>
    </TitleBox>
    <TitleBox>
      <span>$ {item.price}</span>
    </TitleBox>
    <TotalPriceBox>
      <span>Total $ {item.price * item.count}</span>
    </TotalPriceBox>
  </CartItem>
  );
}

const CartItem = styled.div`
  display: inline-block;
  width: 50%;
  padding: 20px;
  border-bottom: 1px solid black;

  div {
    span {
      div {
        color: white;
        span {
          font-size: 20px;
        }
      }
    }
  }

  &:hover {
    div {
      span {
        div {
          color: black;
        }
      }
    }
  }

  svg {
    color: #cdd1d7;
  }

  span {
    svg {
      ${props => console.log(props)}
      color: ${props => props.count === 20 && "#cdd1d7"};
    }
  }

  &:hover {
    svg {
      color: #000;
    }

    span {
      svg {
        color:#cdd1d7;
      }
    }
  }
`;

const Xbox = styled.div`
  position: absolute;
  right: 0;
  border-radius: 100%;
  width: 24px;
  height: 24px;
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;

  span {
    margin-top: 4px;
    font-weight: bold;
  }

  &:hover {
    border: 2px solid #000;
    color: #000;
  }
`;

const CartImgBox = styled.div`
  display: flex;
  justify-content: center;
  width: 100%;
  position: relative;
  padding-bottom: 26px;
`;

const CartImg = styled.img`
  max-height: 160px;
  cursor: pointer;
`;

const CountBox = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  margin-bottom: 10px;

  span {
    margin-left: 15px;
    font-size: 20px;
  }
`;

const TitleBox = styled.div`
  display: flex;
  justify-content: center;
  margin: 3px 0px;

  span {
    font-size: 13px;
  }
`;

const PriceBox = styled.div`
  display: flex;
  justify-content: center;
  margin: 10px 0px;

  span {
    font-size: 13px;
  }
`;

const TotalPriceBox = styled(PriceBox)`
  span {
    font-size: 20px;
  }
`;


const MinusBtn = styled.div`
  cursor: pointer;

  svg {
    color: #cdd1d7;
  }

  &:hover {
    svg {
      fill: ${props => props.count === 1 ? "white" : "black"};
      color: ${props => props.count === 1 ? "red" : "white"};
    }
  }
`;

const PlusBtn = styled.div`
  cursor: pointer;

  svg {
    color: #cdd1d7;
  }

  &:hover {
    svg {
      fill: black;
      color: white;
      width: 50px;
      height: 50px;
    }
  }
`;

function MinusCount({decreaseCount, count}) {
  return (
    <MinusBtn onClick={decreaseCount} count={count}>
      <HiOutlineMinusCircle
        style={{
          verticalAlign: "text-top",
          marginLeft: "3px",
          width: "25px",
          height: "25px",
        }}
      />
    </MinusBtn>
  );
}

function PlusCount({increaseCount, count}) {
  return (
    <PlusBtn onClick={increaseCount} count={count}>
      <HiOutlinePlusCircle
        style={{
          verticalAlign: "text-top",
          marginLeft: "3px",
          width: "25px",
          height: "25px",
        }}
      />
    </PlusBtn>
  );
}

function Xcount() {
  return (
    <HiOutlineX
      style={{
        verticalAlign: "text-top",
        marginRight: "5px",
        width: "22px",
        height: "22px",
        color: "#000",
      }}
    />
  );
}
