import React, { useState, useEffect } from "react";
import styled from "styled-components";
import CartItem from "./components/CartItem";
import PriceContainer from "./components/PriceContainer";
import { cartAPI } from "../../config";

export default function Cart() {
  const [cart, setCart] = useState();
  const [totalPrice, setTotalPrice] = useState(0);

  useEffect(() => {
    fetchCart();
  }, []);

  const fetchCart = async () => {
    // const result = await fetch(cartAPI, {
    //   headers: { Authorization: localStorage.getItem("token") },
    // });
    const result = await fetch(`/Data/cart-Data.json`);
    const { data } = await result.json();
    setCart(data);
  };
  /* eslint-disable react-hooks/exhaustive-deps */
  const totalCalc = () => {
    const price = cart?.products.reduce((accumulator, item) => {
      return accumulator + item.price * item.count;
    }, 0);
    setTotalPrice(price);
  };

  useEffect(() => {
    totalCalc();
  }, [cart, totalCalc]);

  const deleteCart = (i) => {
    const cartList = { ...cart };
    const updateItem = cartList.products.filter(
      (item) => item.id !== cart.products[i].id
    );
    cartList.products = updateItem;
    setCart(cartList);
  };

  const fetchRemove = async (idx) => {
    try {
      const deleteResult = await fetch(cartAPI, {
        method: "delete",
        headers: { Authorization: localStorage.getItem("token") },
        body: JSON.stringify({
          cart_id: cart.products[idx].cart_id,
        }),
      });
      const { message } = await deleteResult.json();
      console.log(message);
    } catch (err) {
      alert("Delete: 서버 연결 실패");
      console.error(err);
    }
  };

  // const fetchRemove = async (idx) => {
  //   const deleteResult = await fetch(cartAPI, {
  //     method: "delete",
  //     headers: { Authorization: localStorage.getItem("token") },
  //     body: JSON.stringify({
  //       cart_id: cart.products[idx].cart_id,
  //     }),
  //   });
  //   try {
  //     const response = (await deleteResult).json();
  //     const result = await response;
  //     console.log(result);
  //   } catch {
  //     alert("Delete: 서버 연결 실패");
  //   }
  // };

  const fetchCount = async (idx) => {
    const patchResult = fetch(cartAPI, {
      method: "patch",
      headers: { Authorization: localStorage.getItem("token") },
      body: JSON.stringify({
        cart_id: cart.products[idx].cart_id,
        count: cart.products[idx].count,
      }),
    });
    try {
      const { message } = await patchResult.json();
      // const result = await response;
      console.log(message);
    } catch {
      alert("patch error");
    }
  };

  const changeCount = (idx, change) => {
    const cartList = { ...cart };
    const updateItem = {
      id: cart.products[idx].id,
      cart_id: cart.products[idx].cart_id,
      title: cart.products[idx].title,
      count: cart.products[idx].count + change,
      price: cart.products[idx].price,
      size: cart.products[idx].size,
      color: cart.products[idx].color,
      serial_num: cart.products[idx].serial_num,
      main_image: cart.products[idx].main_image,
    };
    cartList.products[idx] = updateItem;
    setCart(cartList);

    if (cartList.products[idx].count === 0 && change === -1) {
      fetchRemove(idx);
      deleteCart(idx);
    } else {
      fetchCount(idx);
    }
  };

  return (
    <>
      <Nav>
        <span>진희 Nav</span>
      </Nav>
      <Section>
        <CartContainer>
          <h2>Shopping bag</h2>
          {cart?.products.map((item, idx) => {
            return (
              <CartItem
                key={idx}
                cart={cart}
                idx={idx}
                item={item}
                changeCount={changeCount}
                deleteCart={deleteCart}
              />
            );
          })}
        </CartContainer>
        <PriceContainer totalPrice={totalPrice} />
      </Section>
    </>
  );
}

const Nav = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 60px;
  background-color: yellow;
  text-align: center;
  span {
    font-size: 40px;
  }
`;

const Section = styled.section`
  display: flex;
  justify-content: center;
  max-width: 1000px;
  margin: 0 auto;
`;

const CartContainer = styled.div`
  margin-right: 40px;
  width: calc(66.66% - 20px);
  border-bottom: 2px solid black;
  margin-bottom: 100px;
  height: 100%;
  h2 {
    margin-top: 20px;
    padding-bottom: 13px;
    font-size: 20px;
    border-bottom: 2px solid black;
  }
`;
