import React, { useState } from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import { AiOutlinePlusCircle } from "react-icons/ai";
import {
  FiAlertTriangle,
  FiCloudDrizzle,
  FiEyeOff,
  FiCameraOff,
  FiDollarSign,
} from "react-icons/fi";

export default function ItemDetail({ product, MaterialsItem, ColorItem }) {
  const [madeStatus, setMadeStatus] = useState(true);

  const [careStatus, setCareStatus] = useState(true);

  const [materialStatus, setMaterialStatus] = useState(true);

  const [colorStatus, setColorStatus] = useState(true);

  const initialState = {
    madeStatus: false,
    careStatus: false,
    materialStatus: false,
    colorStatus: false,
  };

  const onMadeBox = () => {
    if (madeStatus) {
      setMadeStatus(false);
      setCareStatus(true);
      setMaterialStatus(true);
      setColorStatus(true);
    } else {
      setMadeStatus(true);
      setCareStatus(true);
      setMaterialStatus(true);
      setColorStatus(true);
    }
  };

  const onCareBox = () => {
    if (careStatus) {
      setMadeStatus(true);
      setCareStatus(false);
      setMaterialStatus(true);
      setColorStatus(true);
    } else {
      setMadeStatus(true);
      setCareStatus(true);
      setMaterialStatus(true);
      setColorStatus(true);
    }
  };

  const onMaterialBox = () => {
    if (materialStatus) {
      setMadeStatus(true);
      setCareStatus(true);
      setMaterialStatus(false);
      setColorStatus(true);
    } else {
      setMadeStatus(true);
      setCareStatus(true);
      setMaterialStatus(true);
      setColorStatus(true);
    }
  };

  const onColorBox = () => {
    if (colorStatus) {
      setMadeStatus(true);
      setCareStatus(true);
      setMaterialStatus(true);
      setColorStatus(false);
    } else {
      setMadeStatus(true);
      setCareStatus(true);
      setMaterialStatus(true);
      setColorStatus(true);
    }
  };

  return (
    <ProductDetail>
      <IdContainer>
        <ProductDetailBox>
          <span>ID</span>
        </ProductDetailBox>
        <span>{product?.product.Serial_Number}</span>
      </IdContainer>
      <DepartmentContainer>
        <ProductDetailBox>
          <span>DEPARTMENT</span>
        </ProductDetailBox>
        <span>Women</span>e
      </DepartmentContainer>
      <DepartmentContainer>
        <ProductDetailBox>
          <span>TYPE</span>
        </ProductDetailBox>
        <span>{product?.product.Type}</span>
      </DepartmentContainer>
      <ProductContainer>
        <ProductDetailBox>
          <span>PRODUCT</span>
        </ProductDetailBox>
        <div>
          <span>{product?.product.Title}</span>
        </div>
      </ProductContainer>
      <MaterialContainer onClick={onMaterialBox}>
        <span>
          <ProductDetailBox>
            <span>MATERIAL</span>
          </ProductDetailBox>
          <span>{product?.product.Material.Material_Name}</span>
          <PlusIcon />
        </span>
        <MaterialsBox
          style={{
            height: materialStatus ? "0px" : "120px",
            margin: materialStatus && "0px",
            padding: materialStatus && "0px",
            border: materialStatus && "0px",
          }}
        >
          <MaterialsItem product={product} />
        </MaterialsBox>
      </MaterialContainer>
      <ColorContainer onClick={onColorBox}>
        <Link to="/item-detail">
          <ProductDetailBox>
            <span>COLOR</span>
          </ProductDetailBox>
          <span>Black</span>
          <PlusIcon />
        </Link>
        <ColorBox
          style={{
            height: colorStatus ? "0px" : "120px",
            margin: colorStatus && "0px",
            padding: colorStatus && "0px",
            border: colorStatus && "0px",
          }}
        >
          <ColorItem product={product} />
        </ColorBox>
      </ColorContainer>
      <MadeContainer onClick={onMadeBox}>
        <span>
          <ProductDetailBox />
          <span>MADE IN TURKEY</span>
          <PlusIcon />
        </span>
        <MadeBox
          style={{
            height: madeStatus ? "0px" : "67px",
            margin: madeStatus && "0px",
            padding: madeStatus && "0px",
            border: madeStatus && "0px",
          }}
        >
          <div>
            <ProductDetailBox>MADE IN</ProductDetailBox>
            <span>TURKEY</span>
          </div>
          <div>
            <ProductDetailBox>SUPPLIER</ProductDetailBox>
            <span>Casa Tekstil</span>
          </div>
        </MadeBox>
      </MadeContainer>

      <CareContainer onClick={onCareBox}>
        <Link to="/item-detail">
          <ProductDetailBox />
          <span>CARE INSTRUCTIONS</span>
          <PlusIcon />
        </Link>
        <CareBox
          style={{
            height: careStatus ? "0px" : "120px",
            margin: careStatus && "0px",
            padding: careStatus && "0px",
            border: careStatus && "0px",
          }}
        >
          <div>
            <FiAlertTriangle />
            <FiCloudDrizzle />
            <FiEyeOff />
            <FiCameraOff />
            <FiDollarSign />
          </div>
          <span>
            Line dry, Iron at low temperature, Only non-chlorine bleach, Wash at
            or below 40°C, Dry clean any solvent except tetrachloroethylene.
          </span>
        </CareBox>
      </CareContainer>
    </ProductDetail>
  );
}

const ProductDetail = styled.div`
  margin-top: 15px;
  transition-timing-function: width 3s ease-in-out;
`;

const IdContainer = styled.div`
  padding: 5px 0px;
`;

const ProductDetailBox = styled.div`
  display: inline-block;
  width: 30%;
  text-align: end;
  margin-right: 12px;
`;

const DepartmentContainer = styled.div`
  padding: 5px 0px;
`;

const ProductContainer = styled.div`
  display: flex;
