import React, { useState, useEffect, useRef } from "react";
import { Link, useLocation, useParams } from "react-router-dom";
import styled from "styled-components";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import OrderContainer from "./components/OrderContainer";
import ProductDetail from "./components/ProductDetail";
import ProductDescription from "./components/ProductDescription";
import { BsArrowRightShort } from "react-icons/bs";
import { IoIosArrowBack, IoIosArrowForward } from "react-icons/io";
import { itemDetailAPI } from "../../config";
import { cartAPI } from "../../config";

export default function ItemDetail() {
  const [product, setProduct] = useState();
  const [size, setSize] = useState({ none: null });
  const [activeSize, setActiveSize] = useState("");
  const [isSoldOut, setIsSoldOut] = useState(false);
  const [viewBag, setViewBag] = useState(false);
  const [timer, setTimer] = useState(0);
  const countTimer = useRef(timer);
  const viewStatus = useRef(false);
  const params = useParams();

  const choiceStatus = (soldOut) => {
    setIsSoldOut(soldOut);
  };

  const isSizeChoice = (size, soldOut) => {
    setActiveSize(size);
    setSize(
      {
        Size: size,
        SoldOut: soldOut,
      },
      choiceStatus(soldOut)
    );
  };

  const fetchItemDetail = async () => {
    const result = await fetch(`${itemDetailAPI}${params}`);
    const { data } = await result.json();
    setProduct(data);
  };

  useEffect(() => {
    fetchItemDetail();
  }, [fetchItemDetail]);

  const fetchAdd = async () => {
    try {
      const addResult = await fetch(cartAPI, {
        method: "post",
        headers: { Authorization: localStorage.getItem("token") },
        body: JSON.stringify({
          product_id: product.product.id,
          count: "1",
          size: size.Size,
        }),
      });
      const { message } = await addResult.json();

      if (message === "SUCCESS") {
        const repeat = setInterval(() => {
          viewStatus.current = !viewStatus.current;
          countTimer.current += 1;
          setTimer(countTimer.current);
          setViewBag(viewStatus.current);

          if (countTimer.current === 2) {
            countTimer.current = 0;
            setTimer(countTimer.current);
            clearInterval(repeat);
          }
        }, 2000);
      }
    } catch {
      alert("Patch: 서버 연결 실패");
    }
  };

  return (
    <>
      <Nav>
        <span>김진희의 Nav</span>
      </Nav>
      <Section>
        <AddToBox>
          <AddToContent viewBag={viewBag}>
            <AddToImg src={product?.Images[0].Img_Url} />
            <div>
              <AddToText>
                <span>ADDED TO BAG</span>
              </AddToText>
              <AddToTitle>
                <span>{product?.product.Title}</span>
                <span>$ {product?.product.Price}</span>
              </AddToTitle>
              <ViewBagBtn>View bag</ViewBagBtn>
            </div>
          </AddToContent>
        </AddToBox>
        <MainContent>
          <ItemInfoBox>
            <ItemRoot>
              <span>
                ARKET
                <ArrowText />
                Women
                <ArrowText />
                {product?.product.Type}
                <ArrowText />
                {product?.product.Title}
              </span>
            </ItemRoot>
            <ItemColors>
              <ItemInfoCategory>COLORS</ItemInfoCategory>
              <ItemCategoryContainer>
                <ColorItem product={product} />
              </ItemCategoryContainer>
            </ItemColors>

            <ItemMaterials>
              <ItemInfoCategory>MATERIALS</ItemInfoCategory>
              <ItemCategoryContainer>
                <MaterialsItem product={product} />
              </ItemCategoryContainer>
            </ItemMaterials>
          </ItemInfoBox>
          <ItemImgBox>
            <StyledSlider {...SETTINGS}>
              {product?.Images.map((productImages, i) => {
                return (
                  <div key={i}>
                    <MainImg src={productImages.Img_Url} />
                  </div>
                );
              })}
            </StyledSlider>
          </ItemImgBox>
          <ItemDetailInfoBox>
            <OrderContainer
              product={product}
              addToBag={fetchAdd}
              size={size}
              isSizeChoice={isSizeChoice}
              activeSize={activeSize}
              isSoldOut={isSoldOut}
              choiceStatus={choiceStatus}
            />
            <ProductDetail
              product={product}
              MaterialsItem={MaterialsItem}
              ColorItem={ColorItem}
            />
            <ProductDescription />
          </ItemDetailInfoBox>
        </MainContent>
      </Section>
    </>
  );
}

const Nav = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 60px;
  background-color: yellow;
  text-align: center;
  span {
    font-size: 40px;
  }
`;

const Section = styled.section`
  margin: 0 auto;
  margin-left: 20px;
  margin-right: 20px;
  padding-top: 20px;
`;

const AddToBox = styled.div`
  position: absolute;
  width: 320px;
  right: 34px;
  top: 62px;
  background-color: white;
