import React, { useState } from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import { AiOutlinePlusCircle } from "react-icons/ai";

export default function ItemDetail({ product }) {
  const [MadeStatus, setMadeStatus] = useState(false);

  const onMadeBox = () => {
    setMadeStatus(!MadeStatus);
  };

  return (
    <ProductDetail>
      <IdContainer>
        <ProductDetailBox>
          <span>ID</span>
        </ProductDetailBox>
        <span>{product?.Serial_Number}</span>
      </IdContainer>
      <DepartmentContainer>
        <ProductDetailBox>
          <span>DEPARTMENT</span>
        </ProductDetailBox>
        <span>Women</span>
      </DepartmentContainer>
      <TypeContainer>
        <ProductDetailBox>
          <span>TYPE</span>
        </ProductDetailBox>
        <span>{product?.Type}</span>
      </TypeContainer>
      <ProductContainer>
        <ProductDetailBox>
          <span>PRODUCT</span>
        </ProductDetailBox>
        <div>
          <span>{product?.Title}</span>
        </div>
      </ProductContainer>
      <MaterialContainer>
        <Link to="/item-detail">
          <ProductDetailBox>
            <span>MATERIAL</span>
          </ProductDetailBox>
          <span>{product?.Material.Material_Name}</span>
          <AiOutlinePlusCircle
            style={{ verticalAlign: "text-top", marginLeft: "3px" }}
          />
        </Link>
      </MaterialContainer>
      <ColorContainer>
        <Link to="/item-detail">
          <ProductDetailBox>
            <span>COLOR</span>
          </ProductDetailBox>
          <span>Black</span>
          <AiOutlinePlusCircle
            style={{ verticalAlign: "text-top", marginLeft: "3px" }}
          />
        </Link>
      </ColorContainer>
      <MadeContainer onClick={onMadeBox}>
        <span>
          <ProductDetailBox />
          <span>MADE IN TURKEY</span>
          <AiOutlinePlusCircle
            style={{ verticalAlign: "text-top", marginLeft: "3px" }}
          />
        </span>
        <MadeBox>
          <div>
            <ProductDetailBox>MADE IN</ProductDetailBox>
            <span>TURKEY</span>
          </div>
          <div>
            <ProductDetailBox>SUPPLIER</ProductDetailBox>
            <span>Casa Tekstil</span>
          </div>
        </MadeBox>
      </MadeContainer>
      {/* {MadeStatus && (
        <MadeBox>
          <div>
            <ProductDetailBox>MADE IN</ProductDetailBox>
            <span>TURKEY</span>
          </div>
          <div>
            <ProductDetailBox>SUPPLIER</ProductDetailBox>
            <span>Casa Tekstil</span>
          </div>
        </MadeBox>
      )} */}

      <CareContainer>
        <Link to="/item-detail">
          <ProductDetailBox />
          <span>CARE INSTRUCTIONS</span>
          <AiOutlinePlusCircle
            style={{ verticalAlign: "text-top", marginLeft: "3px" }}
          />
        </Link>
      </CareContainer>
      <CareBox></CareBox>
    </ProductDetail>
  );
}

const ProductDetail = styled.div`
  margin-top: 15px;
  transition-timing-function: width 3s ease-in-out;
`;

const IdContainer = styled.div`
  padding: 5px 0px;
`;

const ProductDetailBox = styled.div`
  display: inline-block;
  width: 30%;
  text-align: end;
  margin-right: 12px;
`;

const DepartmentContainer = styled.div`
  padding: 5px 0px;
`;

const TypeContainer = styled.div`
  padding: 5px 0px;
`;

const ProductContainer = styled.div`
  display: flex;
  padding: 5px 0px;
  div {
    max-width: 68%;
  }
`;

const MaterialContainer = styled.div`
  padding: 5px 0px;

  &:hover {
    opacity: 0.5;
  }
`;

const ColorContainer = styled.div`
  padding: 5px 0px;

  &:hover {
    opacity: 0.5;
  }
`;

const MadeContainer = styled.div`
  padding: 5px 0px;
  cursor: pointer;

  &:hover {
    opacity: 0.5;
  }
`;

const MadeBox = styled.div`
  margin: 10px 0px;
  border-top: 1px solid #000;
  border-bottom: 1px solid #000;
  padding: 5px 0px;
  opacity: 1;
  transform: translateY(0);
  transition-delay: 0.2s;
  transition-timing-function: ease-in-out;
  transition-duration: 0.2s;
  transition-property: opacity, transform;

  div {
    margin: 5px 0px;

    span {
      margin-left: 15px;
      font-size: 15px;
    }
  }
`;

const CareContainer = styled.div`
  padding: 5px 0px;

  &:hover {
    opacity: 0.5;
  }
`;

const CareBox = styled.div``;
