import React, { useState } from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import { AiOutlinePlusCircle } from "react-icons/ai";
import {
  FiAlertTriangle,
  FiCloudDrizzle,
  FiEyeOff,
  FiCameraOff,
  FiDollarSign,
} from "react-icons/fi";

export default function ItemDetail({ product, MaterialsItem, ColorItem }) {
  // const [madeStatus, setMadeStatus] = useState(true);

  // const [careStatus, setCareStatus] = useState(true);

  // const [materialStatus, setMaterialStatus] = useState(true);

  // const [colorStatus, setColorStatus] = useState(true);

  const initialState = {
    madeStatus: true,
    careStatus: true,
    materialStatus: true,
    colorStatus: true,
  };

  const [selectOpt, SetSelectOpt] = useState(initialState);

  const onOptCheck = (e) => {
    // const { name } = e.target;
    console.log(e.currentTarget.dataset.name);
    SetSelectOpt({
      ...initialState,
      [`${e.currentTarget.dataset.name}Status`]: false,
    });
  };
  console.log(selectOpt.madeStatus);

  // const onMadeBox = () => {
  //   if (madeStatus) {
  //     setMadeStatus(false);
  //     setCareStatus(true);
  //     setMaterialStatus(true);
  //     setColorStatus(true);
  //   } else {
  //     setMadeStatus(true);
  //     setCareStatus(true);
  //     setMaterialStatus(true);
  //     setColorStatus(true);
  //   }
  // };

  // const onCareBox = () => {
  //   if (careStatus) {
  //     setMadeStatus(true);
  //     setCareStatus(false);
  //     setMaterialStatus(true);
  //     setColorStatus(true);
  //   } else {
  //     setMadeStatus(true);
  //     setCareStatus(true);
  //     setMaterialStatus(true);
  //     setColorStatus(true);
  //   }
  // };

  // const onMaterialBox = () => {
  //   if (materialStatus) {
  //     setMadeStatus(true);
  //     setCareStatus(true);
  //     setMaterialStatus(false);
  //     setColorStatus(true);
  //   } else {
  //     setMadeStatus(true);
  //     setCareStatus(true);
  //     setMaterialStatus(true);
  //     setColorStatus(true);
  //   }
  // };

  // const onColorBox = () => {
  //   if (colorStatus) {
  //     setMadeStatus(true);
  //     setCareStatus(true);
  //     setMaterialStatus(true);
  //     setColorStatus(false);
  //   } else {
  //     setMadeStatus(true);
  //     setCareStatus(true);
  //     setMaterialStatus(true);
  //     setColorStatus(true);
  //   }
  // };

  return (
    <ProductDetail>
      <IdContainer>
        <ProductDetailBox>
          <span>ID</span>
        </ProductDetailBox>
        <span>{product?.product.Serial_Number}</span>
      </IdContainer>
      <DepartmentContainer>
        <ProductDetailBox>
          <span>DEPARTMENT</span>
        </ProductDetailBox>
        <span>Women</span>e
      </DepartmentContainer>
      <DepartmentContainer>
        <ProductDetailBox>
          <span>TYPE</span>
        </ProductDetailBox>
        <span>{product?.product.Type}</span>
      </DepartmentContainer>
      <ProductContainer>
        <ProductDetailBox>
          <span>PRODUCT</span>
        </ProductDetailBox>
        <div>
          <span>{product?.product.Title}</span>
        </div>
      </ProductContainer>
      <MaterialContainer>
        <MaterialText onClick={onOptCheck} data-name="material">
          <ProductDetailBox>
            <span>MATERIAL</span>
          </ProductDetailBox>
          <span>{product?.product.Material.Material_Name}</span>
          <PlusIcon />
        </MaterialText>
        <MaterialsBox
          style={{
            height: selectOpt.materialStatus ? "0px" : "120px",
            margin: selectOpt.materialStatus && "0px",
            padding: selectOpt.materialStatus && "0px",
            border: selectOpt.materialStatus && "0px",
          }}
        >
          <MaterialsItem product={product} />
        </MaterialsBox>
      </MaterialContainer>
      <ColorContainer>
        <ColorText to="/item-detail" onClick={onOptCheck} data-name="color">
          <ProductDetailBox>
            <span>COLOR</span>
          </ProductDetailBox>
          <span>Black</span>
          <PlusIcon />
        </ColorText>
        <ColorBox
          style={{
            height: selectOpt.colorStatus ? "0px" : "120px",
            margin: selectOpt.colorStatus && "0px",
            padding: selectOpt.colorStatus && "0px",
            border: selectOpt.colorStatus && "0px",
          }}
        >
          <ColorItem product={product} />
        </ColorBox>
      </ColorContainer>
      <MadeContainer>
