import React, { useState, useEffect } from "react";
import styled, { css } from "styled-components";

export default function ItemDetail() {
  const [product, SetProduct] = useState();

  useEffect(() => {
    fetch("http://localhost:3000/Data/Item-Detail.json", {
      method: "GET",
      // }).then((res) => console.log(res));
    })
      .then((res) => res.json())
      .then((res) => {
        SetProduct(res.product);
      });
  }, []);

  //   console.log(product);

  return (
    <OrderContainer>
      <ProductMaterials>
        <p>{product?.Material.Material_Name}</p>
      </ProductMaterials>
      <ProductInfo>
        <ProducTitle>
          <h1>{product?.Title}</h1>
        </ProducTitle>
        <ProductPrice>
          <span>${product?.Price}</span>
        </ProductPrice>
      </ProductInfo>
      <SizeContainer>
        <SizeGuideBox>
          <span>SIZE</span>
          <span>/</span>
          <span>SIZE GUIDE</span>
        </SizeGuideBox>
        <SizeInfoBox>
          {product?.ProductSize.map((el, i) => {
            return <SizeChoice>{el.Size}</SizeChoice>;
          })}
        </SizeInfoBox>
        <AddBtnBox>
          <span>ADD TO BAG</span>
        </AddBtnBox>
        <ShippingInfo>
          <span>FREE SHIPPING ON ORDERS OVER 200 USD.</span>
        </ShippingInfo>
      </SizeContainer>
    </OrderContainer>
  );
}

const OrderContainer = styled.div``;

const ProductMaterials = styled.div`
  p {
    font-size: 13px;
    padding-bottom: 10px;
  }
`;

const ProductInfo = styled.div`
  display: flex;
  justify-content: space-between;
  padding-bottom: 18px;
  border-bottom: 1px solid #cdd1d7;
`;

const ProducTitle = styled.div`
  display: inline-block;
  h1 {
    max-width: 224px;
    font-size: 20px;
    margin-right: 5px;
  }
`;

const ProductPrice = styled.div`
  display: inline-block;

  span {
    font-size: 20px;
  }
`;

const SizeContainer = styled.div``;

const SizeGuideBox = styled.div`
  padding-top: 10px;
  padding-bottom: 8px;
  span {
    font-size: 13px;
  }
`;

const SizeInfoBox = styled.div`
  margin-top: 8px;
  margin-bottom: 4px;
`;

const SizeChoice = styled.button`
  /* display: flex;
  justify-content: center; */
  display: inline-block;
  width: 40px;
  height: 40px;
  margin-right: 8px;
  margin-bottom: 14px;
  border: 1px solid #000;
  border-radius: 50%;
`;

const AddBtnBox = styled.button`
  width: 100%;
  height: 50px;
  background-color: black;
  margin-bottom: 15px;
  cursor: pointer;
  span {
    color: white;
  }

  &:hover {
    opacity: 0.8;
  }
`;

const ShippingInfo = styled.div`
  text-align: center;
  padding-bottom: 15px;
  border-bottom: 1px solid #000;
  span {
    font-size: 12px;
  }
`;
