import React from 'react';
import styled, {css} from 'styled-components'

export default function ItemDetail() {
  const Section = styled.section` 
  margin: 0 auto;
  margin-left: 20px;
  margin-right: 20px;
  padding-top: 20px;
`;

const MainContent = styled.div`
  display:flex;
  justify-content:space-between;
  /* flex-wrap:wrap; */
`;

const ItemInfoBox = styled.div`
  max-width: 250px;
  width: calc(50% - 299px);
  margin-top: 37px;
  border-top: 2px solid #000;
`;

const ItemImgBox = styled.div`
  width: 100%;
  height: calc(100vh - 56px - 40px);
  background: yellow;
`;

const ItemDetailInfoBox = styled.div`
  max-width: 299px;
  width:100%;
  background: green;
`;

const ItemCategoryContainer = styled.div`
  display:flex;
`;

const ItemColors = styled.div`
  margin-top:15px;
  padding-bottom:4px;
`;

const ItemInfoCategory = styled.h4`
  font-size:13px;
  margin-bottom: 10px;
`;

const ItemColorImgBox = styled.div`
  display:flex;
  align-items:center;
  flex-direction:column;
  width:61px;
  padding-bottom:5px;
`;

const ItemColorImg = styled.img`
  src: "/Images/test_jean.jpg";
  width:49px;
  height: 65px;

`;

const ItemColorName = styled.p`
  font-size:11px;
  text-align:center;
`;

const ItemMaterials = styled.div`
  margin-top:30px;
  padding-top:15px;
  border-top: 2px solid #000;
`;

  return (
  <Section>
    <MainContent>
      <ItemInfoBox>
        <ItemColors>
          <ItemInfoCategory>
            COLORS
          </ItemInfoCategory>
          <ItemCategoryContainer>
            <ItemColorImgBox>
              <ItemColorImg>

              </ItemColorImg>
              <ItemColorName>
                Black
              </ItemColorName>
            </ItemColorImgBox>
            <ItemColorImgBox>
              <ItemColorImg>

              </ItemColorImg>
              <ItemColorName>
                Black
              </ItemColorName>
            </ItemColorImgBox>
            <ItemColorImgBox>
              <ItemColorImg>

              </ItemColorImg>
              <ItemColorName>
                Black
              </ItemColorName>
            </ItemColorImgBox>
          </ItemCategoryContainer>
        </ItemColors>

        <ItemMaterials>
          <ItemInfoCategory>
            MATERIALS
          </ItemInfoCategory>
          <ItemCategoryContainer>

          </ItemCategoryContainer>

        </ItemMaterials>
      </ItemInfoBox>

      <ItemImgBox>

      </ItemImgBox>

      <ItemDetailInfoBox>
      123
      </ItemDetailInfoBox>
      </MainContent>
    </Section>
  );
}
